INSERT INTO `O_regle` VALUES ('1271','1','Appliquer le principe de moindre privilège dès la conception','Interdire par défaut toute action et procéder à l’autorisation exclusive de ce qui estnécessaire aux tâches constitue la stratégie la plus efficace de mise en œuvre duprincipe de moindre privilège. Il convient de s’y conformer autant que possible dèsla phase de conception du composant.','Non conforme','','0000-00-00','','11','15','1.d');
INSERT INTO `O_regle` VALUES ('1272','2','Tenir compte de ses besoins en cloisonnement dès l''initiation d''un projet','Les besoins en cloisonnement d’un composant doivent faire l’objet d’un diagnosticet être considérés comme des besoins au même titre que les besoins fonctionnels, etce dès le début du projet.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1273','3','Préférer des composants implémentant un cloisonnement pertinent','Lors d’un choix entre différentes solutions, celles qui démontrent la meilleure priseen compte du principe du moindre privilège devront être préférées.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1274','4','Garantir l''intégrité des composants de confiance','Il est impératif de garantir concrètement l’intégrité des composants de confiancepour que la sécurité de l’ensemble du composant soit assurée. Des recommandationsplus précises sont fournies en4.1.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1275','5','Rédiger l''analyse de la sécurité attendue du composant','Une analyse de la sécurité attendue du composant doit faire partie des documentsde conception ou intégration mis à disposition.Elle doit comporter les définitions des quatre éléments cités ci-dessus :\nla liste des biens sensibles à protéger;\nle modèle d’attaquant pris en compte (duquel découle en particulier l’identifica-tion des composants de confiance);\nnle périmètre du composant, c’est-à-dire sa surface d’attaque et sa surface de fric-tion avec le système global;\nnles fonctions de sécurité attendues.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1276','6','Caractériser des usages du composant','Réaliser une liste des usages du composant à partir de ses fonctionnalités et de laliste des ressources auxquelles il accède.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1277','7','Minimiser de la surface d''attaque','Réduire systématiquement la surface d’attaque pour chaque usage, de manière à n’-exposer que les interfaces externes utiles pour l’usage considéré.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1278','8','Cloisonner les usages entre eux','Mettre en place un moniteur de référence en s’appuyant sur les composants de con-fiance pour faire en sorte que chaque usage soit confiné dans un domaine.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1279','9','Minimiser la surface de friction','Réduire systématiquement les actions possibles pour chaque domaine aux seuls be-soins liés à l’usage, c’est-à-dire réduire la surface de friction avec le système global.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1280','10','Considérer le moniteur comme un composant de confiance','Vis-à-vis d’un attaquant de la fonction de sécurité de cloisonnement, le moniteur deréférence fait partie des composants de confiance. Ainsi, il doit respecter les recom-mandations de cette section.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1281','11','Identifier les composants de confiance','Tout composant du système qui dispose d’un privilège lui permettant de porter at-teinte à l’intégrité du moniteur, ou des politiques de sécurité qu’il met en place, est àconsidérer comme faisant partie des composants de confiance vis-à-vis du composantanalysé.Tous ces composants doivent donc vérifier les exigences propres aux composants deconfiance.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1282','12','Concevoir des composants simples et concis','Les composants de confiance doivent être conçus en suivant des spécifications claireset complètes, permettant de définir et combiner des éléments simples (suivant leprincipe « Keep It Short and Simple ») qui facilitent développement et validation.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1283','13','Choisir un langage approprié','Utiliser un langage de programmation fortement typé, qui entre autres prévient lesdébordements de tableau, d’entier, ou l’utilisation de pointeurs invalides, est forte-ment souhaitable pour le développement des composants de confiance.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1284','14','Développer selon un référentiel de sécurité','Un référentiel de développement sécurisé doit être utilisé systématiquement lors dudéveloppement de composants de confiance.La vérification du respect du référentiel de codage doit être assurée.Le respect de cette recommandation est critique dans le cas du choix d’un langagene respectant pas les critères conseillés ci-dessus.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1285','15','Auditer le code de l''implémentation des composants de confiance','L’intégralité du code des composants de confiance devra faire l’objet d’un audit decode par des personnels qui n’en sont pas développeurs, de préférence indépendantsdu projet concerné.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1286','16','Valider l''implémentation des composants de confiance','Des tests fonctionnels complets devront permettre de valider le bon fonctionnementdes fonctions de sécurité implémentées. Une attention particulière sera portée àtester aussi bien les opérations qui doivent être refusées que celles qui doivent êtremenées à bien avec succès.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1287','17','Prouver l''implémentation des composants de confiance','Il est fortement recommandé de compléter les tests par une preuve formelle del’absence d’erreur à l’exécution d’un composant de confiance, par exemple à l’aided’outils d’analyse (statique ou dynamique) pour prévenir certains types de vulnérabilités.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1288','18','Supprimer toute partie inutilisée d''un composant de confiance','Un composant de confiance doit être minimal. Dès que possible, la suppression ducode inutilisé sera privilégiée. A défaut, sa désactivation par configuration sera effectuée.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1289','19','Appliquer des techniques de durcissement aux composants de confiance','Les composants de confiance doivent se voir appliquer des techniques de durcisse-ment à l’état de l’art afin de compliquer l’exploitation d’une vulnérabilité et le dé-tournement du flot de contrôle, comme par exemple :\nprésence de motifs d’intégrité de la pile et du tas (canaris);\nprincipe W+X : au cours de toute son utilisation par le système, une zone mé-moire donnée ne doit pas être inscriptible et exécutable, que ce soit simultané-ment ou non;\nrépartition aléatoire de l’espace d’adressage (ou Address Space Layout Random-ization (ASLR)).',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1290','20','Justifier le respect des propriétés fondamentales du moniteur de référence','Le développement, l’intégration et la validation du moniteur de référence doiventpermettre de garantir qu’il vérifie les propriétés suivantes.\n1.Complétude du contrôle exercé: le moniteur est impliqué pour chaque tenta-tive d’accès à une ressource, et ce au moment opportun (i.e. absence d’attaquesTOCTTOU (Time Of Check To Time Of Use), liées à une évolution des propriétésde l’objet entre le moment de la requête au moniteur et l’action effective).\n2.Maintien de l’intégrité du moniteur.\n3.Validation et audit de la politique de sécurité. La possibilité de consulter la poli-tique de sécurité implémentée par le moniteur à un instant donné permettral’audit de celle-ci.Des justifications construites confirmant le respect de ces propriétés doivent figurerdans les documents de conception du composant utilisant le moniteur de référence.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1291','21','Assurer au moniteur de référence un niveau de privilège supérieur à celuides tâches cloisonnées','De manière à préserver l’intégrité du moniteur et des politiques de sécurité qu’il meten application, les tâches cloisonnées ne doivent pas disposer des privilèges néces-saires à la relaxe de la politique de sécurité appliquée.Une façon de garantir ceci est d’assurer que les politiques sont non-modifiables parles tâches cloisonnées et que le moniteur s’exécute à un niveau de privilège supérieurà celui des tâches qu’il cloisonne.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1292','22','Appliquer le principe d''interdiction par défaut','Un moniteur de référence doit être configurable pour que toute action soit interditeà une tâche, sauf à lui être explicitement autorisée.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1293','23','Minimiser le nombre et l''impact des configurations possibles','Dans une démarche de durcissement du moniteur, les options de configuration lais-sées au choix de l’utilisateur (même privilégié) en production doivent être restreintesau strict minimum, de manière à réduire l’impact possible d’une erreur sur la sécuritéglobale du système. Pour les choix laissés à l’utilisateur, les messages d’avertissementsur les conséquences en terme de sécurité doivent être suffisamment explicites.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1294','24','Spécifier le cloisonnement proposé','Pour présenter le cloisonnement mis en place, il est recommandé de procéder de lamanière suivante.\n1.Expliciter les usages identifiés.\n2.Décrire le mécanisme de cloisonnement mis en oeuvre en explicitant ce àquoi correspondent les définitions de tâches, ressources propres et partagéeset moniteur de référence.\n3. Expliciter la politique de sécurité mise en place pour chaque usage.Pour chaque domaine, il faut reprendre la liste des interfaces externes dressées précédem-ment et caractériser les actions autorisées.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1295','25','Assurer l''innocuité du composant pour le système qui l''accueille','Un composant ne doit pas dégrader la sécurité globale du système, notamment enlimitant la mise en place du cloisonnement par d’autres composants utilisés sur lemême système.En particulier, un composant ne doit pas exiger pour fonctionner d’abaisser le niveaude sécurité du système qui l’héberge : le composant doit être compatible avec unniveau de durcissement à l’état de l’art au moment de sa mise en production.',NULL,NULL,NULL,NULL,'11','15','1.d');
INSERT INTO `O_regle` VALUES ('1296','26','Ma règle','Ma description','Non traité','','2020-09-03','','11','15','1.d');
INSERT INTO `O_regle` VALUES ('1297','1','Appliquer le principe de moindre privilège dès la conception','Interdire par défaut toute action et procéder à l’autorisation exclusive de ce qui estnécessaire aux tâches constitue la stratégie la plus efficace de mise en œuvre duprincipe de moindre privilège. Il convient de s’y conformer autant que possible dèsla phase de conception du composant.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1298','2','Tenir compte de ses besoins en cloisonnement dès l''initiation d''un projet','Les besoins en cloisonnement d’un composant doivent faire l’objet d’un diagnosticet être considérés comme des besoins au même titre que les besoins fonctionnels, etce dès le début du projet.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1299','3','Préférer des composants implémentant un cloisonnement pertinent','Lors d’un choix entre différentes solutions, celles qui démontrent la meilleure priseen compte du principe du moindre privilège devront être préférées.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1300','4','Garantir l''intégrité des composants de confiance','Il est impératif de garantir concrètement l’intégrité des composants de confiancepour que la sécurité de l’ensemble du composant soit assurée. Des recommandationsplus précises sont fournies en4.1.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1301','5','Rédiger l''analyse de la sécurité attendue du composant','Une analyse de la sécurité attendue du composant doit faire partie des documentsde conception ou intégration mis à disposition.Elle doit comporter les définitions des quatre éléments cités ci-dessus :\nla liste des biens sensibles à protéger;\nle modèle d’attaquant pris en compte (duquel découle en particulier l’identifica-tion des composants de confiance);\nnle périmètre du composant, c’est-à-dire sa surface d’attaque et sa surface de fric-tion avec le système global;\nnles fonctions de sécurité attendues.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1302','6','Caractériser des usages du composant','Réaliser une liste des usages du composant à partir de ses fonctionnalités et de laliste des ressources auxquelles il accède.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1303','7','Minimiser de la surface d''attaque','Réduire systématiquement la surface d’attaque pour chaque usage, de manière à n’-exposer que les interfaces externes utiles pour l’usage considéré.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1304','8','Cloisonner les usages entre eux','Mettre en place un moniteur de référence en s’appuyant sur les composants de con-fiance pour faire en sorte que chaque usage soit confiné dans un domaine.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1305','9','Minimiser la surface de friction','Réduire systématiquement les actions possibles pour chaque domaine aux seuls be-soins liés à l’usage, c’est-à-dire réduire la surface de friction avec le système global.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1306','10','Considérer le moniteur comme un composant de confiance','Vis-à-vis d’un attaquant de la fonction de sécurité de cloisonnement, le moniteur deréférence fait partie des composants de confiance. Ainsi, il doit respecter les recom-mandations de cette section.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1307','11','Identifier les composants de confiance','Tout composant du système qui dispose d’un privilège lui permettant de porter at-teinte à l’intégrité du moniteur, ou des politiques de sécurité qu’il met en place, est àconsidérer comme faisant partie des composants de confiance vis-à-vis du composantanalysé.Tous ces composants doivent donc vérifier les exigences propres aux composants deconfiance.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1308','12','Concevoir des composants simples et concis','Les composants de confiance doivent être conçus en suivant des spécifications claireset complètes, permettant de définir et combiner des éléments simples (suivant leprincipe « Keep It Short and Simple ») qui facilitent développement et validation.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1309','13','Choisir un langage approprié','Utiliser un langage de programmation fortement typé, qui entre autres prévient lesdébordements de tableau, d’entier, ou l’utilisation de pointeurs invalides, est forte-ment souhaitable pour le développement des composants de confiance.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1310','14','Développer selon un référentiel de sécurité','Un référentiel de développement sécurisé doit être utilisé systématiquement lors dudéveloppement de composants de confiance.La vérification du respect du référentiel de codage doit être assurée.Le respect de cette recommandation est critique dans le cas du choix d’un langagene respectant pas les critères conseillés ci-dessus.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1311','15','Auditer le code de l''implémentation des composants de confiance','L’intégralité du code des composants de confiance devra faire l’objet d’un audit decode par des personnels qui n’en sont pas développeurs, de préférence indépendantsdu projet concerné.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1312','16','Valider l''implémentation des composants de confiance','Des tests fonctionnels complets devront permettre de valider le bon fonctionnementdes fonctions de sécurité implémentées. Une attention particulière sera portée àtester aussi bien les opérations qui doivent être refusées que celles qui doivent êtremenées à bien avec succès.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1313','17','Prouver l''implémentation des composants de confiance','Il est fortement recommandé de compléter les tests par une preuve formelle del’absence d’erreur à l’exécution d’un composant de confiance, par exemple à l’aided’outils d’analyse (statique ou dynamique) pour prévenir certains types de vulnérabilités.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1314','18','Supprimer toute partie inutilisée d''un composant de confiance','Un composant de confiance doit être minimal. Dès que possible, la suppression ducode inutilisé sera privilégiée. A défaut, sa désactivation par configuration sera effectuée.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1315','19','Appliquer des techniques de durcissement aux composants de confiance','Les composants de confiance doivent se voir appliquer des techniques de durcisse-ment à l’état de l’art afin de compliquer l’exploitation d’une vulnérabilité et le dé-tournement du flot de contrôle, comme par exemple :\nprésence de motifs d’intégrité de la pile et du tas (canaris);\nprincipe W+X : au cours de toute son utilisation par le système, une zone mé-moire donnée ne doit pas être inscriptible et exécutable, que ce soit simultané-ment ou non;\nrépartition aléatoire de l’espace d’adressage (ou Address Space Layout Random-ization (ASLR)).',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1316','20','Justifier le respect des propriétés fondamentales du moniteur de référence','Le développement, l’intégration et la validation du moniteur de référence doiventpermettre de garantir qu’il vérifie les propriétés suivantes.\n1.Complétude du contrôle exercé: le moniteur est impliqué pour chaque tenta-tive d’accès à une ressource, et ce au moment opportun (i.e. absence d’attaquesTOCTTOU (Time Of Check To Time Of Use), liées à une évolution des propriétésde l’objet entre le moment de la requête au moniteur et l’action effective).\n2.Maintien de l’intégrité du moniteur.\n3.Validation et audit de la politique de sécurité. La possibilité de consulter la poli-tique de sécurité implémentée par le moniteur à un instant donné permettral’audit de celle-ci.Des justifications construites confirmant le respect de ces propriétés doivent figurerdans les documents de conception du composant utilisant le moniteur de référence.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1317','21','Assurer au moniteur de référence un niveau de privilège supérieur à celuides tâches cloisonnées','De manière à préserver l’intégrité du moniteur et des politiques de sécurité qu’il meten application, les tâches cloisonnées ne doivent pas disposer des privilèges néces-saires à la relaxe de la politique de sécurité appliquée.Une façon de garantir ceci est d’assurer que les politiques sont non-modifiables parles tâches cloisonnées et que le moniteur s’exécute à un niveau de privilège supérieurà celui des tâches qu’il cloisonne.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1318','22','Appliquer le principe d''interdiction par défaut','Un moniteur de référence doit être configurable pour que toute action soit interditeà une tâche, sauf à lui être explicitement autorisée.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1319','23','Minimiser le nombre et l''impact des configurations possibles','Dans une démarche de durcissement du moniteur, les options de configuration lais-sées au choix de l’utilisateur (même privilégié) en production doivent être restreintesau strict minimum, de manière à réduire l’impact possible d’une erreur sur la sécuritéglobale du système. Pour les choix laissés à l’utilisateur, les messages d’avertissementsur les conséquences en terme de sécurité doivent être suffisamment explicites.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1320','24','Spécifier le cloisonnement proposé','Pour présenter le cloisonnement mis en place, il est recommandé de procéder de lamanière suivante.\n1.Expliciter les usages identifiés.\n2.Décrire le mécanisme de cloisonnement mis en oeuvre en explicitant ce àquoi correspondent les définitions de tâches, ressources propres et partagéeset moniteur de référence.\n3. Expliciter la politique de sécurité mise en place pour chaque usage.Pour chaque domaine, il faut reprendre la liste des interfaces externes dressées précédem-ment et caractériser les actions autorisées.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1321','25','Assurer l''innocuité du composant pour le système qui l''accueille','Un composant ne doit pas dégrader la sécurité globale du système, notamment enlimitant la mise en place du cloisonnement par d’autres composants utilisés sur lemême système.En particulier, un composant ne doit pas exiger pour fonctionner d’abaisser le niveaude sécurité du système qui l’héberge : le composant doit être compatible avec unniveau de durcissement à l’état de l’art au moment de sa mise en production.',NULL,NULL,NULL,NULL,'12','14','1.d');
INSERT INTO `O_regle` VALUES ('1322','27','sfsdfsdf','sdfsdfsds','','','0000-00-00','','12','14','1.d');
INSERT INTO `O_regle` VALUES ('1323','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1324','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1325','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1326','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1327','RÈGLE — Utiliser des options de durcissement','5','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1328','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1329','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1330','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1331','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1332','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1333','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1334','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1335','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1336','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1337','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1338','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1339','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1340','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1341','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1342','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1343','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1344','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1345','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1346','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1347','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1348','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1349','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1350','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1351','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1352','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1353','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1354','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1355','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1356','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1357','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1358','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1359','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1360','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1361','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1362','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1363','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1364','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1365','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1366','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1367','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1368','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1369','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1370','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1371','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1372','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1373','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1374','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1375','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1376','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1377','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1378','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1379','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1380','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1381','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1382','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1383','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1384','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1385','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1386','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1387','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1388','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1389','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1390','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1391','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1392','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1393','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1394','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1395','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1396','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1397','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1398','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1399','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1400','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1401','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1402','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1403','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1404','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1405','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1406','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1407','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1408','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1409','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1410','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1411','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1412','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1413','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1414','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1415','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1416','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1417','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1418','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1419','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1420','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1421','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1422','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1423','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1424','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1425','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1426','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1427','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1428','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1429','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1430','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1431','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1432','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1433','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1434','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1435','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1436','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1437','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1438','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1439','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1440','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1441','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1442','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1443','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1444','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1445','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1446','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1447','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1448','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1449','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1450','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1451','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1452','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1453','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1454','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1455','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1456','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1457','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1458','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1459','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1460','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1461','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1462','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1463','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1464','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1465','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1466','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1467','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1468','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1469','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1470','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1471','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1472','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1473','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1474','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1475','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1476','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1477','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1478','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1479','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1480','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1481','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1482','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1483','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1484','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1485','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1486','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1487','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1488','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1489','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1490','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1491','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1492','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1493','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1494','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1495','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1496','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1497','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1498','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1499','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1500','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1501','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1502','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1503','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1504','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard\nLes équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. \nCitons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.\nLes équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : \n> la législation en vigueur ;\n> les principaux risques et menaces ;\n> le maintien en condition de sécurité ;\n> l’authentification et le contrôle d’accès ;\n> le paramétrage fin et le du',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1505','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard\nChaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.\nCes dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :\n>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;\n>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;\n>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, non-réutilisation',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1506','3','Maîtriser les risques de l’infogérance','/ standard\nLorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.\nLes risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.\nEn faveur du bon déroulement des opérations, il s’agit donc :\n>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;\n>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à niveau de la sécurit',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1507','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard\nChaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.\nÀ partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.\nIl s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.) et les ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1508','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard\nLes comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.\n Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :\n>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;\n>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;\n>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.\nIl est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les accès aux éléments ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1509','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard\nLes effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :\n>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;\n>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;\n>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;\n>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;\n>> la gestion des documents et information',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1510','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard\nPour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.\n Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.\nLa sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs besoins. Cito',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1511','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard\nAfin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.\nL’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.\nBien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).\nDans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privilèges, doit ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1512','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard\nCertaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :\n>> de définir quelle population peut y avoir accès ;\n>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;\n>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. \nPar exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, etc. Une revue ré',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1513','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard\nL’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.\nPour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :\n>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;\n>> la désactivation des options de connexion anonyme ;\n>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.\nEn amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1514','11','Protéger les mots de passe stockés sur les systèmes','/Standard\nLa complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.\nOr, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.\nBien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1515','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard\nIl est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.\nLes éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.\nSi le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit afin que ce',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1516','13','Privilégier lorsque c’est possible une authentification forte','/Standard\nIl est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :\n>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;\n>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;\n >> quelque chose que je suis (une empreinte biométrique).\n\n/Renforcé\nLes cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.\nLes cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messagerie, authentification sur ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1517','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard\nL’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :\n>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;\n>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;\n>> chiffrer les partitions où sont stockées les données des utilisateurs ;\n>> désactiver les exécutions automatiques (autorun).\nEn cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications pour des raisons d',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1518','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard\nLes supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.\nS’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.\nIl convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.\n\n/Renforcé\nSur les postes utilisateur, il est recommandé d’utiliser des solutions permettant d’i',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1519','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard\nLa sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.\nL’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.\nPour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.\nAinsi, des politiques de durcissement du système d’explo',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1520','17','Activer et configurer le parefeu local des postes de travail','/Standard\nAprès avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.\nAfin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.\nLes flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.\n\n/Renforcé\nLe filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration et d’assistance uti',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1521','18','Chiffrer les données sensibles transmises par voie Internet','/Standard\nInternet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.\nToutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.\nLa transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1522','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard\nLorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.\nUne zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les différent',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1523','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.\nLa segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.\nDe plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.\nLa protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être complexe et so',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1524','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard\nSi aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.\nBien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.\nD’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communication hist',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1525','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard\nL’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.\nC’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.\n\n/Renforcé\nDes mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité des équipements ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1526','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard\nUne entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.\nDe plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.\nEnfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par un serveu',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1527','24','Protéger sa messagerie professionnelle','/Standard\nLa messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.\nLes utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.\nPour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.\nPar ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès distant à la mess',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1528','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard\nPour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).\nCette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.\nLe partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.\n\n/Renforcé\nPour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connexions partenaires e',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1529','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard\nLes mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.\nLes accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.\nLors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéder au retr',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1530','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard\nUn poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.\nPour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.\n\n/Renforcé\nConcernant les mises à jour logicielles des équipements administrés, elles',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1531','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard\nUn réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. \nSelon les besoins de sécurité de l’entité, il est recommandé :\n>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé\n>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau bureautique ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1532','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard\nDe nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.\nPar conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.\nSi une délégation de privilèges sur un poste de travail est réellement nécessaire ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1533','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard\nLes terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.\nEn tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.\nIl est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux couleurs de ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1534','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard\nLes déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.\nIl faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.\nUne solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.\nEn effet, ces derniers ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1535','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard\nEn situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.\nMême si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.\nPour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.\nPour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile de confiance',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1536','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard\nLes ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.\nLes terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à des a',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1537','34','Définir une politique de mise à jour des composants du système d’information','/Standard\nDe nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.\nCelles-ci doivent notamment préciser :\n>> la manière dont l’inventaire des composants du système d’information est réalisé ;\n>> les sources d’information relatives à la publication des mises à jour ;\n>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;\n>> l’éventuelle qualification d',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1538','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard\nL’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.\nPour anticiper ces obsolescences, un certain nombre de précautions existent :\n>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;\n>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;\n>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;\n>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;\n>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnement d’un logiciel par',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1539','36','Activer et configurer les journaux des composants les plus importants','/Standard\nDisposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.\nLa première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.\nPour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.\nLes évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).\nUne étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :\n>> pare-feu : paquets bloqués ;\n>> systèmes ',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1540','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard\nSuite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.\nCette politique doit au moins intégrer les éléments suivants :\n>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;\n>> les différents types de sauvegarde (par exemple le mode hors ligne) ;\n>> la fréquence des sauvegardes ;\n>> la procédure d’administration et d’exécution des sauvegardes ;\n>> les informations de stockage et les restrictions d’accès aux sauvegardes ;\n>> les procédures de test de restauration ;\n>> la destruction des supports ayant contenu les sauvegardes.\nLes tests de restauration peuvent être réalisés de plusieurs manières :\n>',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1541','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé\nLa réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.\nIls peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.\nÀ l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourront être in',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1542','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard\nToute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.\nCe référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :\n>> définition des règles à appliquer selon le contexte ;\n>> vérification de l’application des règles ;\n>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;\n>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.\nCe référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.\nDans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à aborder dans le cadre d',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1543','40','Définir une procédure de gestion des incidents de sécurité','/Standard\nLe constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.\nUne mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.\nCelui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de la mémoire',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1544','41','Mener une analyse de risques formelle','/Renforcé\nChaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.\nDans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.\nLa démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité d’apparit',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1545','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé\nLa qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.\nAu-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.\nS’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audit de la s',NULL,NULL,NULL,NULL,'15','16','1.d');
INSERT INTO `O_regle` VALUES ('1546','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1547','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1548','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1549','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1550','5','RÈGLE — Utiliser des options de durcissement','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1551','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1552','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1553','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1554','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1555','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1556','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1557','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1558','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1559','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1560','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1561','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1562','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1563','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1564','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1565','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1566','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1567','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1568','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1569','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1570','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1571','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1572','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1573','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1574','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1575','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1576','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1577','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1578','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1579','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1580','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1581','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1582','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1583','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1584','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1585','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1586','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1587','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1588','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1589','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1590','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1591','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1592','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1593','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1594','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1595','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1596','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1597','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1598','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1599','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1600','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1601','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1602','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1603','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1604','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1605','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1606','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1607','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1608','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1609','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1610','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1611','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1612','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1613','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1614','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1615','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1616','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1617','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1618','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1619','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1620','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1621','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1622','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1623','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1624','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1625','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1626','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1627','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1628','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1629','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1630','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1631','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1632','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1633','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1634','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1635','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1636','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1637','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1638','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1639','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1640','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1641','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1642','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1643','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1644','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1645','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1646','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1647','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1648','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1649','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1650','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1651','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1652','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1653','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1654','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1655','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1656','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1657','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1658','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1659','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1660','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1661','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1662','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1663','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1664','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1665','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1666','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1667','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1668','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1669','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1670','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1671','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1672','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1673','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1674','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1675','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1676','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1677','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1678','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1679','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1680','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1681','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1682','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1683','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1684','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1685','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1686','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1687','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1688','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1689','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1690','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1691','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1692','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1693','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1694','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1695','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1696','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1697','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1698','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1699','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1700','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1701','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1702','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1703','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1704','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1705','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1706','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1707','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1708','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1709','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1710','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1711','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1712','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1713','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1714','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1715','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1716','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1717','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1718','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1719','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1720','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1721','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1722','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1723','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1724','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1725','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1726','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'16','17','1.d');
INSERT INTO `O_regle` VALUES ('1727','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard\nLes équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. \nCitons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.\nLes équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : \n> la législation en vigueur ;\n> les principaux risques et menaces ;\n> le maintien en condition de sécurité ;\n> l’authentification et le contrôle d’accès ;\n> le paramétrage fin et le du',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1728','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard\nChaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.\nCes dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :\n>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;\n>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;\n>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, non-réutilisation',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1729','3','Maîtriser les risques de l’infogérance','/ standard\nLorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.\nLes risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.\nEn faveur du bon déroulement des opérations, il s’agit donc :\n>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;\n>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à niveau de la sécurit',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1730','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard\nChaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.\nÀ partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.\nIl s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.) et les ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1731','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard\nLes comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.\n Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :\n>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;\n>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;\n>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.\nIl est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les accès aux éléments ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1732','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard\nLes effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :\n>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;\n>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;\n>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;\n>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;\n>> la gestion des documents et information',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1733','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard\nPour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.\n Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.\nLa sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs besoins. Cito',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1734','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard\nAfin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.\nL’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.\nBien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).\nDans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privilèges, doit ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1735','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard\nCertaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :\n>> de définir quelle population peut y avoir accès ;\n>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;\n>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. \nPar exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, etc. Une revue ré',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1736','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard\nL’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.\nPour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :\n>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;\n>> la désactivation des options de connexion anonyme ;\n>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.\nEn amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1737','11','Protéger les mots de passe stockés sur les systèmes','/Standard\nLa complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.\nOr, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.\nBien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1738','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard\nIl est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.\nLes éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.\nSi le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit afin que ce',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1739','13','Privilégier lorsque c’est possible une authentification forte','/Standard\nIl est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :\n>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;\n>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;\n >> quelque chose que je suis (une empreinte biométrique).\n\n/Renforcé\nLes cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.\nLes cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messagerie, authentification sur ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1740','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard\nL’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :\n>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;\n>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;\n>> chiffrer les partitions où sont stockées les données des utilisateurs ;\n>> désactiver les exécutions automatiques (autorun).\nEn cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications pour des raisons d',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1741','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard\nLes supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.\nS’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.\nIl convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.\n\n/Renforcé\nSur les postes utilisateur, il est recommandé d’utiliser des solutions permettant d’i',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1742','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard\nLa sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.\nL’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.\nPour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.\nAinsi, des politiques de durcissement du système d’explo',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1743','17','Activer et configurer le parefeu local des postes de travail','/Standard\nAprès avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.\nAfin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.\nLes flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.\n\n/Renforcé\nLe filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration et d’assistance uti',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1744','18','Chiffrer les données sensibles transmises par voie Internet','/Standard\nInternet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.\nToutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.\nLa transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1745','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard\nLorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.\nUne zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les différent',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1746','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.\nLa segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.\nDe plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.\nLa protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être complexe et so',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1747','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard\nSi aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.\nBien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.\nD’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communication hist',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1748','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard\nL’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.\nC’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.\n\n/Renforcé\nDes mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité des équipements ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1749','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard\nUne entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.\nDe plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.\nEnfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par un serveu',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1750','24','Protéger sa messagerie professionnelle','/Standard\nLa messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.\nLes utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.\nPour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.\nPar ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès distant à la mess',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1751','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard\nPour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).\nCette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.\nLe partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.\n\n/Renforcé\nPour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connexions partenaires e',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1752','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard\nLes mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.\nLes accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.\nLors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéder au retr',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1753','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard\nUn poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.\nPour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.\n\n/Renforcé\nConcernant les mises à jour logicielles des équipements administrés, elles',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1754','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard\nUn réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. \nSelon les besoins de sécurité de l’entité, il est recommandé :\n>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé\n>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau bureautique ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1755','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard\nDe nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.\nPar conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.\nSi une délégation de privilèges sur un poste de travail est réellement nécessaire ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1756','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard\nLes terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.\nEn tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.\nIl est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux couleurs de ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1757','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard\nLes déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.\nIl faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.\nUne solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.\nEn effet, ces derniers ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1758','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard\nEn situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.\nMême si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.\nPour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.\nPour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile de confiance',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1759','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard\nLes ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.\nLes terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à des a',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1760','34','Définir une politique de mise à jour des composants du système d’information','/Standard\nDe nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.\nCelles-ci doivent notamment préciser :\n>> la manière dont l’inventaire des composants du système d’information est réalisé ;\n>> les sources d’information relatives à la publication des mises à jour ;\n>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;\n>> l’éventuelle qualification d',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1761','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard\nL’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.\nPour anticiper ces obsolescences, un certain nombre de précautions existent :\n>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;\n>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;\n>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;\n>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;\n>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnement d’un logiciel par',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1762','36','Activer et configurer les journaux des composants les plus importants','/Standard\nDisposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.\nLa première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.\nPour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.\nLes évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).\nUne étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :\n>> pare-feu : paquets bloqués ;\n>> systèmes ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1763','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard\nSuite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.\nCette politique doit au moins intégrer les éléments suivants :\n>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;\n>> les différents types de sauvegarde (par exemple le mode hors ligne) ;\n>> la fréquence des sauvegardes ;\n>> la procédure d’administration et d’exécution des sauvegardes ;\n>> les informations de stockage et les restrictions d’accès aux sauvegardes ;\n>> les procédures de test de restauration ;\n>> la destruction des supports ayant contenu les sauvegardes.\nLes tests de restauration peuvent être réalisés de plusieurs manières :\n>',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1764','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé\nLa réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.\nIls peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.\nÀ l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourront être in',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1765','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard\nToute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.\nCe référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :\n>> définition des règles à appliquer selon le contexte ;\n>> vérification de l’application des règles ;\n>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;\n>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.\nCe référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.\nDans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à aborder dans le cadre d',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1766','40','Définir une procédure de gestion des incidents de sécurité','/Standard\nLe constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.\nUne mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.\nCelui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de la mémoire',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1767','41','Mener une analyse de risques formelle','/Renforcé\nChaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.\nDans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.\nLa démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité d’apparit',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1768','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé\nLa qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.\nAu-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.\nS’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audit de la s',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('1769','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard\nLes équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. \nCitons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.\nLes équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : \n> la législation en vigueur ;\n> les principaux risques et menaces ;\n> le maintien en condition de sécurité ;\n> l’authentification et le contrôle d’accès ;\n> le paramétrage fin et le du',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1770','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard\nChaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.\nCes dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :\n>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;\n>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;\n>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, non-réutilisation',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1771','3','Maîtriser les risques de l’infogérance','/ standard\nLorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.\nLes risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.\nEn faveur du bon déroulement des opérations, il s’agit donc :\n>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;\n>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à niveau de la sécurit',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1772','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard\nChaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.\nÀ partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.\nIl s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.) et les ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1773','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard\nLes comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.\n Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :\n>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;\n>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;\n>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.\nIl est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les accès aux éléments ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1774','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard\nLes effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :\n>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;\n>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;\n>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;\n>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;\n>> la gestion des documents et information',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1775','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard\nPour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.\n Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.\nLa sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs besoins. Cito',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1776','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard\nAfin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.\nL’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.\nBien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).\nDans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privilèges, doit ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1777','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard\nCertaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :\n>> de définir quelle population peut y avoir accès ;\n>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;\n>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. \nPar exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, etc. Une revue ré',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1778','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard\nL’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.\nPour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :\n>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;\n>> la désactivation des options de connexion anonyme ;\n>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.\nEn amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1779','11','Protéger les mots de passe stockés sur les systèmes','/Standard\nLa complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.\nOr, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.\nBien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1780','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard\nIl est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.\nLes éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.\nSi le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit afin que ce',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1781','13','Privilégier lorsque c’est possible une authentification forte','/Standard\nIl est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :\n>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;\n>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;\n >> quelque chose que je suis (une empreinte biométrique).\n\n/Renforcé\nLes cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.\nLes cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messagerie, authentification sur ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1782','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard\nL’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :\n>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;\n>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;\n>> chiffrer les partitions où sont stockées les données des utilisateurs ;\n>> désactiver les exécutions automatiques (autorun).\nEn cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications pour des raisons d',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1783','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard\nLes supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.\nS’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.\nIl convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.\n\n/Renforcé\nSur les postes utilisateur, il est recommandé d’utiliser des solutions permettant d’i',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1784','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard\nLa sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.\nL’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.\nPour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.\nAinsi, des politiques de durcissement du système d’explo',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1785','17','Activer et configurer le parefeu local des postes de travail','/Standard\nAprès avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.\nAfin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.\nLes flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.\n\n/Renforcé\nLe filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration et d’assistance uti',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1786','18','Chiffrer les données sensibles transmises par voie Internet','/Standard\nInternet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.\nToutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.\nLa transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1787','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard\nLorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.\nUne zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les différent',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1788','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.\nLa segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.\nDe plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.\nLa protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être complexe et so',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1789','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard\nSi aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.\nBien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.\nD’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communication hist',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1790','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard\nL’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.\nC’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.\n\n/Renforcé\nDes mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité des équipements ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1791','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard\nUne entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.\nDe plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.\nEnfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par un serveu',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1792','24','Protéger sa messagerie professionnelle','/Standard\nLa messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.\nLes utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.\nPour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.\nPar ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès distant à la mess',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1793','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard\nPour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).\nCette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.\nLe partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.\n\n/Renforcé\nPour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connexions partenaires e',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1794','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard\nLes mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.\nLes accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.\nLors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéder au retr',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1795','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard\nUn poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.\nPour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.\n\n/Renforcé\nConcernant les mises à jour logicielles des équipements administrés, elles',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1796','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard\nUn réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. \nSelon les besoins de sécurité de l’entité, il est recommandé :\n>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé\n>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau bureautique ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1797','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard\nDe nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.\nPar conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.\nSi une délégation de privilèges sur un poste de travail est réellement nécessaire ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1798','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard\nLes terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.\nEn tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.\nIl est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux couleurs de ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1799','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard\nLes déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.\nIl faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.\nUne solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.\nEn effet, ces derniers ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1800','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard\nEn situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.\nMême si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.\nPour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.\nPour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile de confiance',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1801','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard\nLes ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.\nLes terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à des a',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1802','34','Définir une politique de mise à jour des composants du système d’information','/Standard\nDe nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.\nCelles-ci doivent notamment préciser :\n>> la manière dont l’inventaire des composants du système d’information est réalisé ;\n>> les sources d’information relatives à la publication des mises à jour ;\n>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;\n>> l’éventuelle qualification d',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1803','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard\nL’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.\nPour anticiper ces obsolescences, un certain nombre de précautions existent :\n>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;\n>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;\n>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;\n>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;\n>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnement d’un logiciel par',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1804','36','Activer et configurer les journaux des composants les plus importants','/Standard\nDisposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.\nLa première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.\nPour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.\nLes évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).\nUne étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :\n>> pare-feu : paquets bloqués ;\n>> systèmes ',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1805','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard\nSuite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.\nCette politique doit au moins intégrer les éléments suivants :\n>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;\n>> les différents types de sauvegarde (par exemple le mode hors ligne) ;\n>> la fréquence des sauvegardes ;\n>> la procédure d’administration et d’exécution des sauvegardes ;\n>> les informations de stockage et les restrictions d’accès aux sauvegardes ;\n>> les procédures de test de restauration ;\n>> la destruction des supports ayant contenu les sauvegardes.\nLes tests de restauration peuvent être réalisés de plusieurs manières :\n>',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1806','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé\nLa réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.\nIls peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.\nÀ l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourront être in',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1807','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard\nToute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.\nCe référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :\n>> définition des règles à appliquer selon le contexte ;\n>> vérification de l’application des règles ;\n>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;\n>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.\nCe référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.\nDans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à aborder dans le cadre d',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1808','40','Définir une procédure de gestion des incidents de sécurité','/Standard\nLe constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.\nUne mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.\nCelui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de la mémoire',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1809','41','Mener une analyse de risques formelle','/Renforcé\nChaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.\nDans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.\nLa démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité d’apparit',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1810','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé\nLa qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.\nAu-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.\nS’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audit de la s',NULL,NULL,NULL,NULL,'18','15','1.d');
INSERT INTO `O_regle` VALUES ('1811','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1812','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1813','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1814','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1815','5','RÈGLE — Utiliser des options de durcissement','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1816','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1817','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1818','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1819','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1820','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1821','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1822','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1823','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1824','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1825','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1826','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1827','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1828','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1829','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1830','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1831','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1832','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1833','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1834','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1835','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1836','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1837','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1838','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1839','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1840','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1841','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1842','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1843','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1844','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1845','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1846','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1847','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1848','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1849','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1850','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1851','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1852','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1853','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1854','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1855','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1856','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1857','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1858','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1859','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1860','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1861','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1862','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1863','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1864','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1865','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1866','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1867','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1868','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1869','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1870','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1871','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1872','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1873','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1874','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1875','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1876','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1877','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1878','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1879','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1880','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1881','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1882','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1883','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1884','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1885','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1886','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1887','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1888','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1889','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1890','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1891','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1892','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1893','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1894','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1895','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1896','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1897','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1898','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1899','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1900','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1901','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1902','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1903','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1904','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1905','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1906','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1907','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1908','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1909','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1910','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1911','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1912','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1913','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1914','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1915','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1916','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1917','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1918','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1919','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1920','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1921','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1922','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1923','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1924','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1925','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1926','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1927','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1928','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1929','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1930','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1931','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1932','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1933','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1934','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1935','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1936','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1937','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1938','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1939','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1940','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1941','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1942','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1943','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1944','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1945','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1946','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1947','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1948','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1949','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1950','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1951','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1952','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1953','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1954','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1955','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1956','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1957','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1958','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1959','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1960','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1961','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1962','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1963','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1964','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1965','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1966','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1967','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1968','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1969','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1970','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1971','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1972','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1973','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1974','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1975','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1976','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1977','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1978','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1979','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1980','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1981','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1982','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1983','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1984','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1985','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1986','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1987','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1988','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1989','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1990','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1991','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1992','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1993','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1994','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1995','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1996','5','RÈGLE — Utiliser des options de durcissement','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1997','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1998','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('1999','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2000','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2001','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2002','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2003','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2004','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2005','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2006','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2007','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2008','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2009','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2010','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2011','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2012','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2013','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2014','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2015','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2016','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2017','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2018','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2019','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2020','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2021','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2022','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2023','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2024','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2025','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2026','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2027','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2028','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2029','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2030','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2031','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2032','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2033','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2034','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2035','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2036','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2037','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2038','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2039','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2040','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2041','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2042','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2043','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2044','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2045','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2046','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2047','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2048','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2049','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2050','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2051','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2052','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2053','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2054','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2055','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2056','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2057','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2058','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2059','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2060','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2061','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2062','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2063','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2064','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2065','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2066','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2067','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2068','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2069','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2070','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2071','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2072','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2073','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2074','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2075','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2076','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2077','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2078','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2079','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2080','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2081','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2082','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2083','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2084','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2085','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2086','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2087','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2088','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2089','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2090','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2091','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2092','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2093','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2094','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2095','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2096','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2097','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2098','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2099','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2100','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2101','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2102','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2103','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2104','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2105','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2106','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2107','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2108','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2109','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2110','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2111','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2112','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2113','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2114','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2115','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2116','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2117','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2118','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2119','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2120','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2121','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2122','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2123','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2124','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2125','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2126','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2127','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2128','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2129','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2130','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2131','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2132','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2133','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2134','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2135','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2136','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2137','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2138','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2139','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2140','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2141','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2142','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2143','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2144','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2145','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2146','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2147','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2148','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2149','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2150','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2151','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2152','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2153','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2154','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2155','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2156','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2157','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2158','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2159','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2160','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2161','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2162','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2163','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2164','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2165','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2166','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2167','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2168','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2169','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2170','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2171','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2172','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2173','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2174','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2175','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2176','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2177','5','RÈGLE — Utiliser des options de durcissement','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2178','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2179','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2180','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2181','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2182','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2183','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2184','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2185','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2186','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2187','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2188','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2189','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2190','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2191','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2192','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2193','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2194','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2195','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2196','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2197','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2198','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2199','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2200','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2201','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2202','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2203','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2204','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2205','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2206','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2207','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2208','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2209','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2210','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2211','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2212','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2213','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2214','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2215','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2216','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2217','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2218','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2219','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2220','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2221','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2222','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2223','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2224','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2225','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2226','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2227','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2228','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2229','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2230','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2231','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2232','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2233','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2234','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2235','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2236','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2237','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2238','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2239','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2240','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2241','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2242','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2243','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2244','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2245','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2246','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2247','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2248','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2249','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2250','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2251','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2252','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2253','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2254','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2255','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2256','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2257','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2258','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2259','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2260','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2261','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2262','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2263','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2264','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2265','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2266','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2267','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2268','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2269','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2270','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2271','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2272','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2273','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2274','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2275','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2276','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2277','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2278','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2279','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2280','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2281','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2282','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2283','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2284','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2285','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2286','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2287','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2288','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2289','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2290','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2291','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2292','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2293','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2294','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2295','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2296','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2297','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2298','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2299','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2300','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2301','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2302','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2303','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2304','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2305','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2306','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2307','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2308','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2309','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2310','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2311','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2312','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2313','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2314','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2315','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2316','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2317','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2318','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2319','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2320','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2321','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2322','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2323','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2324','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2325','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2326','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2327','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2328','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2329','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2330','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2331','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2332','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2333','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2334','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2335','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2336','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2337','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2338','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2339','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2340','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2341','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2342','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2343','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2344','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2345','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2346','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2347','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2348','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2349','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2350','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2351','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2352','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2353','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2354','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2355','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2356','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2357','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2358','5','RÈGLE — Utiliser des options de durcissement','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2359','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2360','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2361','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2362','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2363','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2364','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2365','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2366','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2367','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2368','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2369','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2370','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2371','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2372','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2373','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2374','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2375','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2376','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2377','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2378','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2379','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2380','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2381','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2382','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2383','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2384','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2385','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2386','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2387','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2388','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2389','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2390','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2391','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2392','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2393','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2394','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2395','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2396','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2397','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2398','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2399','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2400','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2401','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2402','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2403','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2404','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2405','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2406','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2407','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2408','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2409','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2410','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2411','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2412','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2413','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2414','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2415','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2416','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2417','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2418','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2419','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2420','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2421','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2422','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2423','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2424','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2425','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2426','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2427','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2428','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2429','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2430','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2431','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2432','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2433','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2434','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2435','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2436','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2437','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2438','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2439','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2440','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2441','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2442','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2443','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2444','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2445','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2446','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2447','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2448','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2449','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2450','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2451','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2452','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2453','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2454','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2455','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2456','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2457','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2458','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2459','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2460','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2461','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2462','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2463','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2464','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2465','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2466','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2467','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2468','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2469','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2470','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2471','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2472','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2473','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2474','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2475','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2476','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2477','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2478','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2479','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2480','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2481','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2482','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2483','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2484','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2485','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2486','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2487','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2488','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2489','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2490','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2491','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2492','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2493','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2494','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2495','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2496','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2497','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2498','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2499','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2500','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2501','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2502','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2503','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2504','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2505','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2506','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2507','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2508','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2509','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2510','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2511','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2512','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2513','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2514','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2515','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2516','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2517','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2518','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2519','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2520','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2521','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2522','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2523','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2524','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2525','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2526','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2527','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2528','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2529','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2530','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2531','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2532','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2533','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2534','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'14','16','1.d');
INSERT INTO `O_regle` VALUES ('2535','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard\nLes équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. \nCitons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.\nLes équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : \n> la législation en vigueur ;\n> les principaux risques et menaces ;\n> le maintien en condition de sécurité ;\n> l’authentification et le contrôle d’accès ;\n> le paramétrage fin et le du',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2536','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard\nChaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.\nCes dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :\n>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;\n>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;\n>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, non-réutilisation',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2537','3','Maîtriser les risques de l’infogérance','/ standard\nLorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.\nLes risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.\nEn faveur du bon déroulement des opérations, il s’agit donc :\n>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;\n>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à niveau de la sécurit',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2538','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard\nChaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.\nÀ partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.\nIl s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.) et les ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2539','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard\nLes comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.\n Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :\n>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;\n>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;\n>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.\nIl est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les accès aux éléments ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2540','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard\nLes effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :\n>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;\n>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;\n>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;\n>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;\n>> la gestion des documents et information',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2541','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard\nPour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.\n Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.\nLa sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs besoins. Cito',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2542','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard\nAfin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.\nL’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.\nBien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).\nDans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privilèges, doit ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2543','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard\nCertaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :\n>> de définir quelle population peut y avoir accès ;\n>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;\n>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. \nPar exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, etc. Une revue ré',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2544','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard\nL’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.\nPour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :\n>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;\n>> la désactivation des options de connexion anonyme ;\n>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.\nEn amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2545','11','Protéger les mots de passe stockés sur les systèmes','/Standard\nLa complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.\nOr, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.\nBien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2546','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard\nIl est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.\nLes éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.\nSi le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit afin que ce',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2547','13','Privilégier lorsque c’est possible une authentification forte','/Standard\nIl est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :\n>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;\n>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;\n >> quelque chose que je suis (une empreinte biométrique).\n\n/Renforcé\nLes cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.\nLes cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messagerie, authentification sur ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2548','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard\nL’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :\n>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;\n>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;\n>> chiffrer les partitions où sont stockées les données des utilisateurs ;\n>> désactiver les exécutions automatiques (autorun).\nEn cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications pour des raisons d',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2549','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard\nLes supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.\nS’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.\nIl convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.\n\n/Renforcé\nSur les postes utilisateur, il est recommandé d’utiliser des solutions permettant d’i',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2550','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard\nLa sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.\nL’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.\nPour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.\nAinsi, des politiques de durcissement du système d’explo',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2551','17','Activer et configurer le parefeu local des postes de travail','/Standard\nAprès avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.\nAfin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.\nLes flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.\n\n/Renforcé\nLe filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration et d’assistance uti',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2552','18','Chiffrer les données sensibles transmises par voie Internet','/Standard\nInternet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.\nToutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.\nLa transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2553','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard\nLorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.\nUne zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les différent',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2554','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.\nLa segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.\nDe plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.\nLa protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être complexe et so',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2555','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard\nSi aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.\nBien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.\nD’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communication hist',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2556','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard\nL’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.\nC’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.\n\n/Renforcé\nDes mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité des équipements ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2557','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard\nUne entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.\nDe plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.\nEnfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par un serveu',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2558','24','Protéger sa messagerie professionnelle','/Standard\nLa messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.\nLes utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.\nPour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.\nPar ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès distant à la mess',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2559','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard\nPour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).\nCette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.\nLe partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.\n\n/Renforcé\nPour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connexions partenaires e',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2560','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard\nLes mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.\nLes accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.\nLors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéder au retr',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2561','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard\nUn poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.\nPour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.\n\n/Renforcé\nConcernant les mises à jour logicielles des équipements administrés, elles',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2562','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard\nUn réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. \nSelon les besoins de sécurité de l’entité, il est recommandé :\n>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé\n>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau bureautique ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2563','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard\nDe nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.\nPar conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.\nSi une délégation de privilèges sur un poste de travail est réellement nécessaire ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2564','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard\nLes terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.\nEn tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.\nIl est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux couleurs de ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2565','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard\nLes déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.\nIl faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.\nUne solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.\nEn effet, ces derniers ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2566','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard\nEn situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.\nMême si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.\nPour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.\nPour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile de confiance',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2567','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard\nLes ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.\nLes terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à des a',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2568','34','Définir une politique de mise à jour des composants du système d’information','/Standard\nDe nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.\nCelles-ci doivent notamment préciser :\n>> la manière dont l’inventaire des composants du système d’information est réalisé ;\n>> les sources d’information relatives à la publication des mises à jour ;\n>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;\n>> l’éventuelle qualification d',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2569','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard\nL’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.\nPour anticiper ces obsolescences, un certain nombre de précautions existent :\n>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;\n>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;\n>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;\n>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;\n>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnement d’un logiciel par',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2570','36','Activer et configurer les journaux des composants les plus importants','/Standard\nDisposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.\nLa première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.\nPour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.\nLes évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).\nUne étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :\n>> pare-feu : paquets bloqués ;\n>> systèmes ',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2571','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard\nSuite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.\nCette politique doit au moins intégrer les éléments suivants :\n>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;\n>> les différents types de sauvegarde (par exemple le mode hors ligne) ;\n>> la fréquence des sauvegardes ;\n>> la procédure d’administration et d’exécution des sauvegardes ;\n>> les informations de stockage et les restrictions d’accès aux sauvegardes ;\n>> les procédures de test de restauration ;\n>> la destruction des supports ayant contenu les sauvegardes.\nLes tests de restauration peuvent être réalisés de plusieurs manières :\n>',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2572','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé\nLa réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.\nIls peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.\nÀ l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourront être in',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2573','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard\nToute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.\nCe référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :\n>> définition des règles à appliquer selon le contexte ;\n>> vérification de l’application des règles ;\n>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;\n>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.\nCe référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.\nDans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à aborder dans le cadre d',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2574','40','Définir une procédure de gestion des incidents de sécurité','/Standard\nLe constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.\nUne mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.\nCelui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de la mémoire',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2575','41','Mener une analyse de risques formelle','/Renforcé\nChaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.\nDans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.\nLa démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité d’apparit',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2576','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé\nLa qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.\nAu-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.\nS’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audit de la s',NULL,NULL,NULL,NULL,'19','25','1.d');
INSERT INTO `O_regle` VALUES ('2577','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard<br>Les équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. <br>Citons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.<br>Les équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : <br>> la législation en vigueur ;<br>> les principaux risques et menaces ;<br>> le maintien en condition de sécurité ;<br>> l’authentification et le contrôle d’accès ;<br>> le ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2578','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard<br>Chaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.<br>Ces dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :<br>>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;<br>>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;<br>>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, no',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2579','3','Maîtriser les risques de l’infogérance','/ standard<br>Lorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.<br>Les risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.<br>En faveur du bon déroulement des opérations, il s’agit donc :<br>>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;<br>>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à nivea',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2580','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard<br>Chaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.<br>À partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.<br>Il s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2581','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard<br>Les comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.<br> Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :<br>>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;<br>>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;<br>>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.<br>Il est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les a',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2582','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard<br>Les effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :<br>>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;<br>>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;<br>>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;<br>>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;<br>>> la gestion des docume',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2583','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard<br>Pour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.<br> Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.<br>La sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs beso',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2584','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard<br>Afin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.<br>L’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.<br>Bien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).<br>Dans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privi',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2585','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard<br>Certaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :<br>>> de définir quelle population peut y avoir accès ;<br>>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;<br>>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. <br>Par exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, et',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2586','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard<br>L’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.<br>Pour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :<br>>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;<br>>> la désactivation des options de connexion anonyme ;<br>>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.<br>En amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2587','11','Protéger les mots de passe stockés sur les systèmes','/Standard<br>La complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.<br>Or, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.<br>Bien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2588','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard<br>Il est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.<br>Les éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.<br>Si le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit af',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2589','13','Privilégier lorsque c’est possible une authentification forte','/Standard<br>Il est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :<br>>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;<br>>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;<br> >> quelque chose que je suis (une empreinte biométrique).<br><br>/Renforcé<br>Les cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.<br>Les cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messageri',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2590','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard<br>L’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :<br>>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;<br>>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;<br>>> chiffrer les partitions où sont stockées les données des utilisateurs ;<br>>> désactiver les exécutions automatiques (autorun).<br>En cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2591','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard<br>Les supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.<br>S’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.<br>Il convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.<br><br>/Renforcé<br>Sur les postes utilisateur, il est recommandé d’utiliser des soluti',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2592','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard<br>La sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.<br>L’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.<br>Pour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.<br>Ainsi, des politiques de durcissement du sys',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2593','17','Activer et configurer le parefeu local des postes de travail','/Standard<br>Après avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.<br>Afin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.<br>Les flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.<br><br>/Renforcé<br>Le filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration e',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2594','18','Chiffrer les données sensibles transmises par voie Internet','/Standard<br>Internet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.<br>Toutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.<br>La transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2595','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard<br>Lorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.<br>Une zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les dif',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2596','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.<br>La segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.<br>De plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.<br>La protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être compl',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2597','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard<br>Si aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.<br>Bien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.<br>D’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communica',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2598','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard<br>L’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.<br>C’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.<br><br>/Renforcé<br>Des mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité d',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2599','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard<br>Une entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.<br>De plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.<br>Enfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2600','24','Protéger sa messagerie professionnelle','/Standard<br>La messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.<br>Les utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.<br>Pour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.<br>Par ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès dista',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2601','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard<br>Pour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).<br>Cette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.<br>Le partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.<br><br>/Renforcé<br>Pour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connex',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2602','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard<br>Les mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.<br>Les accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.<br>Lors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéde',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2603','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard<br>Un poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.<br>Pour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.<br><br>/Renforcé<br>Concernant les mises à jour logicielles des équipements adm',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2604','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard<br>Un réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. <br>Selon les besoins de sécurité de l’entité, il est recommandé :<br>>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé<br>>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2605','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard<br>De nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.<br>Par conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.<br>Si une délégation de privilèges sur un poste de travail est réellement né',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2606','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard<br>Les terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.<br>En tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.<br>Il est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux cou',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2607','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard<br>Les déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.<br>Il faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.<br>Une solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.<br>En effet, c',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2608','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard<br>En situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.<br>Même si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.<br>Pour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.<br>Pour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2609','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard<br>Les ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.<br>Les terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2610','34','Définir une politique de mise à jour des composants du système d’information','/Standard<br>De nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.<br>Celles-ci doivent notamment préciser :<br>>> la manière dont l’inventaire des composants du système d’information est réalisé ;<br>>> les sources d’information relatives à la publication des mises à jour ;<br>>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;<br>>> l’éventuel',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2611','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard<br>L’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.<br>Pour anticiper ces obsolescences, un certain nombre de précautions existent :<br>>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;<br>>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;<br>>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;<br>>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;<br>>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnem',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2612','36','Activer et configurer les journaux des composants les plus importants','/Standard<br>Disposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.<br>La première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.<br>Pour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.<br>Les évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).<br>Une étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :<br>>> pare-feu : paquets bloq',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2613','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard<br>Suite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.<br>Cette politique doit au moins intégrer les éléments suivants :<br>>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;<br>>> les différents types de sauvegarde (par exemple le mode hors ligne) ;<br>>> la fréquence des sauvegardes ;<br>>> la procédure d’administration et d’exécution des sauvegardes ;<br>>> les informations de stockage et les restrictions d’accès aux sauvegardes ;<br>>> les procédures de test de restauration ;<br>>> la destruction des supports ayant contenu les sauvegardes.<br>Les tests de restauration peuvent être réal',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2614','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé<br>La réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.<br>Ils peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.<br>À l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourron',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2615','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard<br>Toute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.<br>Ce référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :<br>>> définition des règles à appliquer selon le contexte ;<br>>> vérification de l’application des règles ;<br>>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;<br>>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.<br>Ce référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.<br>Dans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2616','40','Définir une procédure de gestion des incidents de sécurité','/Standard<br>Le constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.<br>Une mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.<br>Celui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de l',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2617','41','Mener une analyse de risques formelle','/Renforcé<br>Chaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.<br>Dans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.<br>La démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité ',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2618','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé<br>La qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.<br>Au-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.<br>S’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audi',NULL,NULL,NULL,NULL,'17','17','1.d');
INSERT INTO `O_regle` VALUES ('2619','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard<br>Les équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. <br>Citons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.<br>Les équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : <br>> la législation en vigueur ;<br>> les principaux risques et menaces ;<br>> le maintien en condition de sécurité ;<br>> l’authentification et le contrôle d’accès ;<br>> le ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2620','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard<br>Chaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.<br>Ces dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :<br>>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;<br>>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;<br>>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, no',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2621','3','Maîtriser les risques de l’infogérance','/ standard<br>Lorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.<br>Les risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.<br>En faveur du bon déroulement des opérations, il s’agit donc :<br>>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;<br>>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à nivea',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2622','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard<br>Chaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.<br>À partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.<br>Il s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2623','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard<br>Les comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.<br> Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :<br>>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;<br>>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;<br>>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.<br>Il est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les a',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2624','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard<br>Les effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :<br>>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;<br>>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;<br>>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;<br>>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;<br>>> la gestion des docume',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2625','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard<br>Pour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.<br> Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.<br>La sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs beso',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2626','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard<br>Afin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.<br>L’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.<br>Bien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).<br>Dans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privi',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2627','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard<br>Certaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :<br>>> de définir quelle population peut y avoir accès ;<br>>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;<br>>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. <br>Par exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, et',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2628','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard<br>L’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.<br>Pour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :<br>>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;<br>>> la désactivation des options de connexion anonyme ;<br>>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.<br>En amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2629','11','Protéger les mots de passe stockés sur les systèmes','/Standard<br>La complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.<br>Or, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.<br>Bien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2630','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard<br>Il est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.<br>Les éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.<br>Si le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit af',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2631','13','Privilégier lorsque c’est possible une authentification forte','/Standard<br>Il est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :<br>>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;<br>>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;<br> >> quelque chose que je suis (une empreinte biométrique).<br><br>/Renforcé<br>Les cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.<br>Les cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messageri',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2632','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard<br>L’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :<br>>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;<br>>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;<br>>> chiffrer les partitions où sont stockées les données des utilisateurs ;<br>>> désactiver les exécutions automatiques (autorun).<br>En cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2633','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard<br>Les supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.<br>S’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.<br>Il convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.<br><br>/Renforcé<br>Sur les postes utilisateur, il est recommandé d’utiliser des soluti',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2634','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard<br>La sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.<br>L’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.<br>Pour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.<br>Ainsi, des politiques de durcissement du sys',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2635','17','Activer et configurer le parefeu local des postes de travail','/Standard<br>Après avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.<br>Afin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.<br>Les flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.<br><br>/Renforcé<br>Le filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration e',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2636','18','Chiffrer les données sensibles transmises par voie Internet','/Standard<br>Internet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.<br>Toutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.<br>La transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2637','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard<br>Lorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.<br>Une zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les dif',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2638','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.<br>La segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.<br>De plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.<br>La protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être compl',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2639','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard<br>Si aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.<br>Bien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.<br>D’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communica',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2640','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard<br>L’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.<br>C’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.<br><br>/Renforcé<br>Des mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité d',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2641','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard<br>Une entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.<br>De plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.<br>Enfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2642','24','Protéger sa messagerie professionnelle','/Standard<br>La messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.<br>Les utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.<br>Pour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.<br>Par ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès dista',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2643','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard<br>Pour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).<br>Cette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.<br>Le partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.<br><br>/Renforcé<br>Pour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connex',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2644','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard<br>Les mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.<br>Les accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.<br>Lors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéde',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2645','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard<br>Un poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.<br>Pour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.<br><br>/Renforcé<br>Concernant les mises à jour logicielles des équipements adm',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2646','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard<br>Un réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. <br>Selon les besoins de sécurité de l’entité, il est recommandé :<br>>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé<br>>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2647','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard<br>De nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.<br>Par conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.<br>Si une délégation de privilèges sur un poste de travail est réellement né',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2648','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard<br>Les terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.<br>En tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.<br>Il est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux cou',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2649','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard<br>Les déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.<br>Il faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.<br>Une solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.<br>En effet, c',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2650','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard<br>En situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.<br>Même si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.<br>Pour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.<br>Pour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2651','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard<br>Les ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.<br>Les terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2652','34','Définir une politique de mise à jour des composants du système d’information','/Standard<br>De nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.<br>Celles-ci doivent notamment préciser :<br>>> la manière dont l’inventaire des composants du système d’information est réalisé ;<br>>> les sources d’information relatives à la publication des mises à jour ;<br>>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;<br>>> l’éventuel',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2653','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard<br>L’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.<br>Pour anticiper ces obsolescences, un certain nombre de précautions existent :<br>>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;<br>>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;<br>>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;<br>>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;<br>>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnem',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2654','36','Activer et configurer les journaux des composants les plus importants','/Standard<br>Disposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.<br>La première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.<br>Pour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.<br>Les évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).<br>Une étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :<br>>> pare-feu : paquets bloq',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2655','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard<br>Suite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.<br>Cette politique doit au moins intégrer les éléments suivants :<br>>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;<br>>> les différents types de sauvegarde (par exemple le mode hors ligne) ;<br>>> la fréquence des sauvegardes ;<br>>> la procédure d’administration et d’exécution des sauvegardes ;<br>>> les informations de stockage et les restrictions d’accès aux sauvegardes ;<br>>> les procédures de test de restauration ;<br>>> la destruction des supports ayant contenu les sauvegardes.<br>Les tests de restauration peuvent être réal',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2656','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé<br>La réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.<br>Ils peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.<br>À l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourron',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2657','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard<br>Toute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.<br>Ce référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :<br>>> définition des règles à appliquer selon le contexte ;<br>>> vérification de l’application des règles ;<br>>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;<br>>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.<br>Ce référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.<br>Dans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2658','40','Définir une procédure de gestion des incidents de sécurité','/Standard<br>Le constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.<br>Une mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.<br>Celui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de l',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2659','41','Mener une analyse de risques formelle','/Renforcé<br>Chaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.<br>Dans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.<br>La démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2660','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé<br>La qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.<br>Au-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.<br>S’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audi',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2661','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard<br>Les équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. <br>Citons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.<br>Les équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : <ul> <li>> la législation en vigueur ;</li><li>> les principaux risques et menaces ;</li><ul><br>> le maintien en condition de sécurité ;<br>> l’authentification et le contrôle',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2662','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard<br>Chaque utilisateur est un maillon à part entière de la chaîne des systèmes d’information. À ce titre et dès son arrivée dans l’entité, il doit être informé des enjeux de sécurité, des règles à respecter et des bons comportements à adopter en matière de sécurité des systèmes d’information à travers des actions de sensibilisation et de formation.<br>Ces dernières doivent être régulières, adaptées aux utilisateurs ciblés, peuvent prendre différentes formes (mails, affichage, réunions, espace intranet dédié,etc.) et aborder au minimum les sujets suivants :<br>>> les objectifs et enjeux que rencontre l’entité en matière de sécurité dessystèmes d’information ;<br>>> les informations considérées comme sensibles ;>> les réglementations et obligations légales ;<br>>> les règles et consignes de sécurité régissant l’activité quotidienne : respect de la politique de sécurité, non-connexion d’équipements personnels au réseau de l’entité, non-divulgation de mots de passe à un tiers, no',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2663','3','Maîtriser les risques de l’infogérance','/ standard<br>Lorsqu’une entité souhaite externaliser son système d’information ou ses données, elle doit en amont évaluer les risques spécifiques à l’infogérance (maîtrise du système d’information, actions à distance, hébergement mutualisé, etc.) afin de prendre en compte, dès la rédaction des exigences applicables au futur prestataire, les besoins et mesures de sécurité adaptés.<br>Les risques SSI inhérents à ce type de démarche peuvent être liés au contexte de l’opération d’externalisation mais aussi à des spécifications contractuelles déficientes ou incomplètes.<br>En faveur du bon déroulement des opérations, il s’agit donc :<br>>> d’étudier attentivement les conditions des offres, la possibilité de les adapter à des besoins spécifiques et les limites de responsabilité du prestataire ;<br>>> d’imposer une liste d’exigences précises au prestataire : réversibilité du contrat, réalisation d’audits, sauvegarde et restitution des données dans un format ouvert normalisé, maintien à nivea',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2664','4','Identifier les informations et serveurs les plus sensibles et maintenir un schéma du réseau','/Standard<br>Chaque entité possède des données sensibles. Ces dernières peuvent porter sur son activité propre (propriété intellectuelle, savoir-faire, etc.) ou sur ses clients, administrés ou usagers (données personnelles, contrats, etc.). Afin de pouvoir les protéger efficacement, il est indispensable de les identifier.<br>À partir de cette liste de données sensibles, il sera possible de déterminer sur quels composants du système d’information elles se localisent (bases de données, partages de fichiers, postes de travail, etc.). Ces composants correspondent aux serveurs et postes critiques pour l’entité. À ce titre, ils devront faire l’objet de mesures de sécurité spécifiques pouvant porter sur la sauvegarde, la journalisation, les accès, etc.<br>Il s’agit donc de créer et de maintenir à jour un schéma simplifié du réseau (ou cartographie) représentant les différentes zones IP et le plan d’adressage associé, les équipements de routage et de sécurité (pare-feu, relais applicatifs,etc.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2665','5','Disposer d’un inventaire exhaustif des comptes privilégiés et le maintenir à jour','/Standard<br>Les comptes bénéficiant de droits spécifiques sont des cibles privilégiées par les attaquants qui souhaitent obtenir un accès le plus large possible au système d’information. Ils doivent donc faire l’objet d’une attention toute particulière.<br> Il s’agit pour cela d’effectuer un inventaire de ces comptes, de le mettre à jour régulièrement et d’y renseigner les informations suivantes :<br>>> les utilisateurs ayant un compte administrateur ou des droits supérieurs à ceux d’un utilisateur standard sur le système d’information ;<br>>> les utilisateurs disposant de suffisamment de droits pour accéder aux répertoires de travail des responsables ou de l’ensemble des utilisateurs ;<br>>> les utilisateurs utilisant un poste non administré par le service informatique et qui ne fait pas l’objet de mesures de sécurité édictées par la politique de sécurité générale de l’entité.<br>Il est fortement recommandé de procéder à une revue périodique de ces comptes afin de s’assurer que les a',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2666','6','Organiser les procédures d’arrivée, de départ et de changement de fonction des utilisateurs','/ Standard<br>Les effectifs d’une entité, qu’elle soit publique ou privée, évoluent sans cesse : arrivées, départs, mobilité interne. Il est par conséquent nécessaire que les droits et les accès au système d’information soient mis à jour en fonction de ces évolutions. Il est notamment essentiel que l’ensemble des droits affectés à une personne soient révoqués lors de son départ ou en cas de changement de fonction. Les procédures d’arrivée et de départ doivent donc être définies, en lien avec la fonction ressources humaines. Elles doivent au minimum prendre en compte :<br>>> la création et la suppression des comptes informatiques et boîtes aux lettres associées ;<br>>> les droits et accès à attribuer et retirer à une personne dont la fonction change ;<br>>> la gestion des accès physiques aux locaux (attribution, restitution des badges et des clés, etc.) ;<br>>> l’affectation des équipements mobiles (ordinateur portable, clé USB, disque dur, ordiphone, etc.) ;<br>>> la gestion des docume',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2667','7','Autoriser la connexion au réseau de l’entité aux seuls équipements maîtrisés','/Standard<br>Pour garantir la sécurité de son système d’information, l’entité doit maîtriser les équipements qui s’y connectent, chacun constituant un point d’entrée potentiellement vulnérable. Les équipements personnels (ordinateurs portables, tablettes, ordiphones, etc.) sont, par définition, difficilement maîtrisables dans la mesure où ce sont les utilisateurs qui décident de leur niveau de sécurité. De la même manière, la sécurité des équipements dont sont dotés les visiteurs échappe à tout contrôle de l’entité.<br> Seule la connexion de terminaux maîtrisés par l’entité doit être autorisée sur ses différents réseaux d’accès, qu’ils soient filaire ou sans fil. Cette recommandation, avant tout d’ordre organisationnel, est souvent perçue comme inacceptable ou rétrograde. Cependant, y déroger fragilise le réseau de l’entité et sert ainsi les intérêts d’un potentiel attaquant.<br>La sensibilisation des utilisateurs doit donc s’accompagner de solutions pragmatiques répondant à leurs beso',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2668','8','Identifier nommément chaque personne accédant au système et distinguer les rôles utilisateur /admini','/Standard<br>Afin de faciliter l’attribution d’une action sur le système d’information en cas d’incident ou d’identifier d’éventuels comptes compromis, les comptes d’accès doivent être nominatifs.<br>L’utilisation de comptes génériques (ex : admin, user) doit être marginale et ceux-ci doivent pouvoir être rattachés à un nombre limité de personnes physiques.<br>Bien entendu, cette règle n’interdit pas le maintien de comptes de service, rattachés à un processus informatique (ex : apache, mysqld).<br>Dans tous les cas, les comptes génériques et de service doivent être gérés selon une politique au moins aussi stricte que celle des comptes nominatifs. Par ailleurs, un compte d’administration nominatif, distinct du compte utilisateur, doit être attribué à chaque administrateur. Les identifiants et secrets d’authentification doivent être différents (ex : pmartin comme identifiant utilisateur, adm-pmartin comme identifiant administrateur). Ce compte d’administration, disposant de plus de privi',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2669','9','Attribuer les bons droits sur les ressources sensibles du système d’information','/Standard<br>Certaines des ressources du système peuvent constituer une source d’information précieuse aux yeux d’un attaquant (répertoires contenant des données sensibles, bases de données, boîtes aux lettres électroniques, etc.). Il est donc primordial d’établir une liste précise de ces ressources et pour chacune d’entre elles :<br>>> de définir quelle population peut y avoir accès ;<br>>> de contrôler strictement son accès, en s’assurant que les utilisateurs sont authentifiés et font partie de la population ciblée ;<br>>> d’éviter sa dispersion et sa duplication à des endroits non maîtrisés ou soumis à un contrôle d’accès moins strict. <br>Par exemple, les répertoires des administrateurs regroupant de nombreuses informations sensibles doivent faire l’objet d’un contrôle d’accès précis. Il en va de même pour les informations sensibles présentes sur des partages réseau : exports de fichiers de configuration, documentation technique du système d’information, bases de données métier, et',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2670','10','Définir et vérifier des règles de choix et de dimensionnement des mots de passe','/Standard<br>L’ANSSI énonce un ensemble de règles et de bonnes pratiques en matière de choix et de dimensionnement des mots de passe. Parmi les plus critiques de ces règles figure la sensibilisation des utilisateurs aux risques liés au choix d’un mot de passe qui serait trop facile à deviner, ou encore la réutilisation de mots de passe d’une application à l’autre et plus particulièrement entre messageries personnelles et professionnelles.<br>Pour encadrer et vérifier l’application de ces règles de choix et de dimensionnement, l’entité pourra recourir à différentes mesures parmi lesquelles :<br>>> le blocage des comptes à l’issue de plusieurs échecs de connexion ;<br>>> la désactivation des options de connexion anonyme ;<br>>> l’utilisation d’un outil d’audit de la robustesse des mots de passe.<br>En amont de telles procédures, un effort de communication visant à expliquer le sens de ces règles et éveiller les consciences sur leur importance est fondamental.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2671','11','Protéger les mots de passe stockés sur les systèmes','/Standard<br>La complexité, la diversité ou encore l’utilisation peu fréquente de certains mots de passe, peuvent encourager leur stockage sur un support physique (mémo, post-it) ou numérique (fichiers de mots de passe, envoi par mail à soimême, recours aux boutons « Se souvenir du mot de passe ») afin de pallier tout oubli ou perte.<br>Or, les mots de passe sont une cible privilégiée par les attaquants désireux d’accéder au système, que cela fasse suite à un vol ou à un éventuel partage du support de stockage. C’est pourquoi ils doivent impérativement être protégés au moyen de solutions sécurisées au premier rang desquelles figurent l’utilisation d’un coffre-fort numérique et le recours à des mécanismes de chiffrement.<br>Bien entendu, le choix d’un mot de passe pour ce coffre-fort numérique doit respecter les règles énoncées précédemment et être mémorisé par l’utilisateur, qui n’a plus que celui-ci à retenir.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2672','12','Changer les éléments d’authentification par défaut sur les équipements et services','/Standard<br>Il est impératif de partir du principe que les configurations par défaut des systèmes d’information sont systématiquement connues des attaquants, quand bien même celles-ci ne le sont pas du grand public. Ces configurations se révèlent (trop) souvent triviales (mot de passe identique à l’identifiant, mal dimensionné ou commun à l’ensemble des équipements et services par exemple) et sont, la plupart du temps, faciles à obtenir pour des attaquants capables de se faire passer pour un utilisateur légitime.<br>Les éléments d’authentification par défaut des composants du système doivent donc être modifiés dès leur installation et, s’agissant de mots de passe, être conformes aux recommandations précédentes en matière de choix, de dimensionnement et de stockage.<br>Si le changement d’un identifiant par défaut se révèle impossible pour cause, par exemple, de mot de passe ou certificat « en dur » dans un équipement, ce problème critique doit être signalé au distributeur du produit af',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2673','13','Privilégier lorsque c’est possible une authentification forte','/Standard<br>Il est vivement recommandé de mettre en oeuvre une authentification forte nécessitant l’utilisation de deux facteurs d’authentification différents parmi les suivants :<br>>> quelque chose que je sais (mot de passe, tracé de déverrouillage, signature) ;<br>>> quelque chose que je possède (carte à puce, jeton USB, carte magnétique, RFID, un téléphone pour recevoir un code SMS) ;<br> >> quelque chose que je suis (une empreinte biométrique).<br><br>/Renforcé<br>Les cartes à puces doivent être privilégiées ou, à défaut, les mécanismes de mots de passe à usage unique (ou One Time Password) avec jeton physique. Les opérations cryptographiques mises en place dans ces deux facteurs offrent généralement de bonnes garanties de sécurité.<br>Les cartes à puce peuvent être plus complexes à mettre en place car nécessitant une infrastructure de gestion des clés adaptée. Elles présentent cependant l’avantage d’être réutilisables à plusieurs fins : chiffrement, authentification de messageri',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2674','14','Mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique','/Standard<br>L’utilisateur plus ou moins au fait des bonnes pratiques de sécurité informatique est, dans de très nombreux cas, la première porte d’entrée des attaquants vers le système. Il est donc fondamental de mettre en place un niveau de sécurité minimal sur l’ensemble du parc informatique de l’entité (postes utilisateurs, serveurs, imprimantes, téléphones, périphériques USB, etc.) en implémentant les mesures suivantes :<br>>> limiter les applications installées et modules optionnels des navigateurs web aux seuls nécessaires ;<br>>> doter les postes utilisateurs d’un pare-feu local et d’un anti-virus (ceux-ci sont parfois inclus dans le système d’exploitation) ;<br>>> chiffrer les partitions où sont stockées les données des utilisateurs ;<br>>> désactiver les exécutions automatiques (autorun).<br>En cas de dérogation nécessaire aux règles de sécurité globales applicables aux postes, ceux-ci doivent être isolés du système (s’il est impossible de mettre à jour certaines applications ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2675','15','Se protéger des menaces relatives à l’utilisation de supports amovibles','/Standard<br>Les supports amovibles peuvent être utilisés afin de propager des virus, voler des informations sensibles et stratégiques ou encore compromettre le réseau de l’entité. De tels agissements peuvent avoir des conséquences désastreuses pour l’activité de la structure ciblée.<br>S’il n’est pas question d’interdire totalement l’usage de supports amovibles au sein de l’entité, il est néanmoins nécessaire de traiter ces risques en identifiant des mesures adéquates et en sensibilisant les utilisateurs aux risques que ces supports peuvent véhiculer.<br>Il convient notamment de proscrire le branchement de clés USB inconnues (ramassées dans un lieu public par exemple) et de limiter au maximum celui de clés non maîtrisées (dont on connait la provenance mais pas l’intégrité) sur le système d’information à moins, dans ce dernier cas, de faire inspecter leur contenu par l’antivirus du poste de travail.<br><br>/Renforcé<br>Sur les postes utilisateur, il est recommandé d’utiliser des soluti',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2676','16','Utiliser un outil de gestion centralisée afin d’homogénéiser les politiques de sécurité','/Standard<br>La sécurité du système d’information repose sur la sécurité du maillon le plus faible. Il est donc nécessaire d’homogénéiser la gestion des politiques de sécurité s’appliquant à l’ensemble du parc informatique de l’entité.<br>L’application de ces politiques (gestion des mots de passe, restrictions de connexions sur certains postes sensibles, configuration des navigateurs Web, etc.) doit être simple et rapide pour les administrateurs, en vue notamment de faciliter la mise en oeuvre de contre-mesures en cas de crise informatique.<br>Pour cela, l’entité pourra se doter d’un outil de gestion centralisée (par exemple Active Directory en environnement Microsoft) auquel il s’agit d’inclure le plus grand nombre d’équipements informatiques possible. Les postes de travail et les serveurs sont concernés par cette mesure qui nécessite éventuellement en amont un travail d’harmonisation des choix de matériels et de systèmes d’exploitation.<br>Ainsi, des politiques de durcissement du sys',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2677','17','Activer et configurer le parefeu local des postes de travail','/Standard<br>Après avoir réussi à prendre le contrôle d’un poste de travail (à cause, par exemple, d’une vulnérabilité présente dans le navigateur Internet), un attaquant cherchera souvent à étendre son intrusion aux autres postes de travail pour, in fine, accéder aux documents des utilisateurs.<br>Afin de rendre plus difficile ce déplacement latéral de l’attaquant, il est nécessaire d’activer le pare-feu local des postes de travail au moyen de logiciels intégrés (pare-feu local Windows) ou spécialisés.<br>Les flux de poste à poste sont en effet très rares dans un réseau bureautique classique : les fichiers sont stockés dans des serveurs de fichiers, les applications accessibles sur des serveurs métier, etc.<br><br>/Renforcé<br>Le filtrage le plus simple consiste à bloquer l’accès aux ports d’administration par défaut des postes de travail (ports TCP 135, 445 et 3389 sous Windows, port TCP 22 sous Unix), excepté depuis les ressources explicitement identifiées (postes d’administration e',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2678','18','Chiffrer les données sensibles transmises par voie Internet','/Standard<br>Internet est un réseau sur lequel il est quasi impossible d’obtenir des garanties sur le trajet que vont emprunter les données que l’on y envoie. Il est donc tout à fait possible qu’un attaquant se trouve sur le trajet de données transitant entre deux correspondants.<br>Toutes les données envoyées par courriel ou transmises au moyen d’outils d’hébergement en ligne (Cloud) sont par conséquent vulnérables. Il s’agit donc de procéder à leur chiffrement systématique avant de les adresser à un correspondant ou de les héberger.<br>La transmission du secret (mot de passe, clé, etc.) permettant alors de déchiffrer les données, si elle est nécessaire, doit être effectuée via un canal de confiance ou, à défaut, un canal distinct du canal de transmission des données. Ainsi, si les données chiffrées sont transmises par courriel, une remise en main propre du mot de passe ou, à défaut, par téléphone doit être privilégiée.',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2679','19','Segmenter le réseau et mettre en place un cloisonnement entre ces zones','/Standard<br>Lorsque le réseau est " à plat ", sans aucun mécanisme de cloisonnement, chaque machine du réseau peut accéder à n’importe quelle autre machine.La compromission de l’une d’elles met alors en péril l’ensemble des machines connectées. Un attaquant peut ainsi compromettre un poste utilisateur et ensuite " rebondir " jusqu’à des serveurs critiques. Il est donc important, dès la conception de l’architecture réseau, de raisonner par segmentation en zones composées de systèmes ayant des besoins de sécurité homogènes. On pourra par exemple regrouper distinctement des serveurs d’infrastructure, des serveurs métiers, des postes de travail utilisateurs, des postes de travail administrateurs, des postes de téléphonie sur IP, etc.<br>Une zone se caractérise alors par des VLAN et des sous-réseaux IP dédiés voire par des infrastructures dédiées selon sa criticité. Ainsi, des mesures de cloisonnement telles qu’un filtrage IP à l’aide d’un pare-feu peuvent être mises en place entre les dif',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2680','20','S’assurer de la sécurité des réseaux d’accès Wi-Fi et de la séparation des usages','/Standard/nL’usage du Wi-Fi en milieu professionnel est aujourd’hui démocratisé mais présente toujours des risques de sécurité bien spécifiques : faibles garanties en matière de disponibilité, pas de maîtrise de la zone de couverture pouvant mener à une attaque hors du périmètre géographique de l’entité, configuration par défaut des points d’accès peu sécurisée, etc.<br>La segmentation de l’architecture réseau doit permettre de limiter les conséquences d’une intrusion par voie radio à un périmètre déterminé du système d’information. Les flux en provenance des postes connectés au réseau d’accès Wi-Fi doivent donc être filtrés et restreints aux seuls flux nécessaires.<br>De plus, il est important d’avoir recours prioritairement à un chiffrement robuste (mode WPA2, algorithme AES CCMP) et à une authentification centralisée, si possible par certificats clients des machines.<br>La protection du réseau Wi-Fi par un mot de passe unique et partagé est déconseillée. À défaut, il doit être compl',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2681','21','Utiliser des protocoles réseaux sécurisés dès qu’ils existent','/Standard<br>Si aujourd’hui la sécurité n’est plus optionnelle, cela n’a pas toujours été le cas. C’est pourquoi de nombreux protocoles réseaux ont dû évoluer pour intégrer cette composante et répondre aux besoins de confidentialité et d’intégrité qu’impose l’échange de données. Les protocoles réseaux sécurisés doivent être utilisés dès que possible, que ce soit sur des réseaux publics (Internet par exemple) ou sur le réseau interne de l’entité.<br>Bien qu’il soit difficile d’en dresser une liste exhaustive, les protocoles les plus courants reposent sur l’utilisation de TLS et sont souvent identifiables par l’ajout de la lettre « s » (pour secure en anglais) à l’acronyme du protocole.Citons par exemple https pour la navigation Web ou IMAPS, SMTPS ou POP3S pour la messagerie.<br>D’autres protocoles ont été conçus de manière sécurisée dès la conception pour se substituer à d’anciens protocoles non sécurisés. Citons par exemple SSH (Secure SHell) venu remplacer les protocoles de communica',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2682','22','Mettre en place une passerelle d’accès sécurisé à Internet','/Standard<br>L’accès à Internet, devenu indispensable, présente des risques importants : sites Web hébergeant du code malveillant, téléchargement de fichiers « toxiques » et, par conséquent, possible prise de contrôle du terminal, fuite de données sensibles, etc. Pour sécuriser cet usage, il est donc indispensable que les terminaux utilisateurs n’aient pas d’accès réseau direct à Internet.<br>C’est pourquoi il est recommandé de mettre en oeuvre une passerelle sécurisée d’accès à Internet comprenant au minimum un pare-feu au plus près de l’accès Internet pour filtrer les connexions et un serveur mandataire (proxy) embarquant différents mécanismes de sécurité. Celui-ci assure notamment l’authentification des utilisateurs et la journalisation des requêtes.<br><br>/Renforcé<br>Des mécanismes complémentaires sur le serveur mandataire pourront être activés selon les besoins de l’entité : analyse antivirus du contenu, filtrage par catégories d’URLs, etc. Le maintien en condition de sécurité d',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2683','23','Cloisonner les services visibles depuis Internet du reste du système d’information','/Standard<br>Une entité peut choisir d’héberger en interne des services visibles sur Internet (site web, serveur de messagerie, etc.). Au regard de l’évolution et du perfectionnement des cyberattaques sur Internet, il est essentiel de garantir un haut niveau de protection de ce service avec des administrateurs compétents, formés de manière continue (à l’état de l’art des technologies en la matière) et disponibles. Dans le cas contraire, le recours à un hébergement externalisé auprès de professionnels est à privilégier.<br>De plus, les infrastructures d’hébergement Internet doivent être physiquement cloisonnées de toutes les infrastructures du système d’information qui n’ont pas vocation à être visibles depuis Internet.<br>Enfin, il convient de mettre en place une infrastructure d’interconnexion de ces services avec Internet permettant de filtrer les flux liés à ces services de manière distincte des autres flux de l’entité. Il s’agit également d’imposer le passage des flux entrants par ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2684','24','Protéger sa messagerie professionnelle','/Standard<br>La messagerie est le principal vecteur d’infection du poste de travail, qu’il s’agisse de l’ouverture de pièces jointes contenant un code malveillant ou du clic malencontreux sur un lien redirigeant vers un site lui-même malveillant.<br>Les utilisateurs doivent être particulièrement sensibilisés à ce sujet : l’expéditeur est-il connu ? Une information de sa part est-elle attendue ? Le lien proposé est-il cohérent avec le sujet évoqué ? En cas de doute, une vérification de l’authenticité du message par un autre canal (téléphone, SMS, etc.) est nécessaire.<br>Pour se prémunir d’escroqueries (ex : demande de virement frauduleux émanant vraisemblablement d’un dirigeant), des mesures organisationnelles doivent être appliquées strictement.<br>Par ailleurs, la redirection de messages professionnels vers une messagerie personnelle est à proscrire car cela constitue une fuite irrémédiable d’informations de l’entité. Si nécessaire des moyens maîtrisés et sécurisés pour l’accès dista',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2685','25','Sécuriser les interconnexions réseau dédiées avec les partenaires','/Standard<br>Pour des besoins opérationnels, une entité peut être amenée à établir une interconnexion réseau dédiée avec un fournisseur ou un client (ex : infogérance, échange de données informatisées, flux monétiques, etc.).<br>Cette interconnexion peut se faire au travers d’un lien sur le réseau privé de l’entité ou directement sur Internet. Dans le second cas, il convient d’établir un tunnel site à site, de préférence IPsec, en respectant les préconisations de l’ANSSI.<br>Le partenaire étant considéré par défaut comme non sûr, il est indispensable d’effectuer un filtrage IP à l’aide d’un pare-feu au plus près de l’entrée des flux sur le réseau de l’entité. La matrice des flux (entrants et sortants) devra être réduite au juste besoin opérationnel, maintenue dans le temps et la configuration des équipements devra y être conforme.<br><br>/Renforcé<br>Pour des entités ayant des besoins de sécurité plus exigeants, il conviendra de s’assurer que l’équipement de filtrage IP pour les connex',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2686','26','Contrôler et protéger l’accès aux salles serveurs et aux locaux techniques','/Standard<br>Les mécanismes de sécurité physique doivent faire partie intégrante de la sécurité des systèmes d’information et être à l’état de l’art afin de s’assurer qu’ils ne puissent pas être contournés aisément par un attaquant. Il convient donc d’identifier les mesures de sécurité physique adéquates et de sensibiliser continuellement les utilisateurs aux risques engendrés par le contournement des règles.<br>Les accès aux salles serveurs et aux locaux techniques doivent être contrôlés à l’aide de serrures ou de mécanismes de contrôle d’accès par badge. Les accès non accompagnés des prestataires extérieurs aux salles serveurs et aux locaux techniques sont à proscrire, sauf s’il est possible de tracer strictement les accès et de limiter ces derniers en fonction des plages horaires. Une revue des droits d’accès doit être réalisée régulièrement afin d’identifier les accès non autorisés.<br>Lors du départ d’un collaborateur ou d’un changement de prestataire, il est nécessaire de procéde',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2687','27','Interdire l’accès à Internet depuis les postes ou serveurs utilisés pour l’administration du système','/Standard<br>Un poste de travail ou un serveur utilisé pour les actions d’administration ne doit en aucun cas avoir accès à Internet, en raison des risques que la navigation Web (à travers des sites contenant du code malveillant) et la messagerie (au travers de pièces jointes potentiellement vérolées) font peser sur son intégrité.<br>Pour les autres usages des administrateurs nécessitant Internet (consultation de documentation en ligne, de leur messagerie, etc.), il est recommandé de mettre à leur disposition un poste de travail distinct. À défaut, l’accès à une infrastructure virtualisée distante pour la bureautique depuis un poste d’administration est envisageable. La réciproque consistant à fournir un accès distant à une infrastructure d’administration depuis un poste bureautique est déconseillée car elle peut mener à une élévation de privilèges en cas de récupération des authentifiants d’administration.<br><br>/Renforcé<br>Concernant les mises à jour logicielles des équipements adm',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2688','28','Utiliser un réseau dédié et cloisonné pour l’administration du système d’information','/Standard<br>Un réseau d’administration interconnecte, entre autres, les postes ou serveurs d’administration et les interfaces d’administration des équipements. Dans la logique de segmentation du réseau global de l’entité, il est indispensable de cloisonner spécifiquement le réseau d’administration, notamment vis-à-vis du réseau bureautique des utilisateurs, pour se prémunir de toute compromission par rebond depuis un poste utilisateur vers une ressource d’administration. <br>Selon les besoins de sécurité de l’entité, il est recommandé :<br>>> de privilégier en premier lieu un cloisonnement physique des réseaux dès que cela est possible, cette solution pouvant représenter des coûts et un temps de déploiement importants ;/Renforcé<br>>> à défaut, de mettre en oeuvre un cloisonnement logique cryptographique reposant sur la mise en place de tunnels IPsec. Ceci permet d’assurer l’intégrité et la confidentialité des informations véhiculées sur le réseau d’administration vis-à-vis du réseau ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2689','29','Limiter au strict besoin opérationnel les droits d’administration sur les postes de travail','/Standard<br>De nombreux utilisateurs, y compris au sommet des hiérarchies, sont tentés de demander à leur service informatique de pouvoir disposer, par analogie avec leur usage personnel, de privilèges plus importants sur leurs postes de travail : installation de logiciels, configuration du système, etc. Par défaut,il est recommandé qu’un utilisateur du SI, quelle que soit sa position hiérarchique et ses attributions, ne dispose pas de privilèges d’administration sur son poste de travail. Cette mesure, apparemment contraignante, vise à limiter les conséquences de l’exécution malencontreuse d’un code malveillant. La mise à disposition d’un magasin étoffé d’applications validées par l’entité du point de vue de la sécurité permettra de répondre à la majorité des besoins.<br>Par conséquent, seuls les administrateurs chargés de l’administration des postes doivent disposer de ces droits lors de leurs interventions.<br>Si une délégation de privilèges sur un poste de travail est réellement né',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2690','30','Prendre des mesures de sécurisation physique des terminaux nomades','/Standard<br>Les terminaux nomades (ordinateurs portables, tablettes, ordiphones) sont, par nature, exposés à la perte et au vol. Ils peuvent contenir localement des informations sensibles pour l’entité et constituer un point d’entrée vers de plus amples ressources du système d’information. Au-delà de l’application au minimum des politiques de sécurité de l’entité, des mesures spécifiques de sécurisation de ces équipements sont donc à prévoir.<br>En tout premier lieu, les utilisateurs doivent être sensibilisés pour augmenter leur niveau de vigilance lors de leurs déplacements et conserver leurs équipements à portée de vue. N’importe quelle entité, même de petite taille, peut être victime d’une attaque informatique. Dès lors, en mobilité, tout équipement devient une cible potentielle voire privilégiée.<br>Il est recommandé que les terminaux nomades soient aussi banalisés que possible en évitant toute mention explicite de l’entité d’appartenance (par l’apposition d’un autocollant aux cou',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2691','31','Chiffrer les données sensibles, en particulier sur le matériel potentiellement perdable','/Standard<br>Les déplacements fréquents en contexte professionnel et la miniaturisation du matériel informatique conduisent souvent à la perte ou au vol de celui-ci dans l’espace public. Cela peut porter atteinte aux données sensibles de l’entité qui y sont stockées.<br>Il faut donc ne stocker que des données préalablement chiffrées sur l’ensemble des matériels nomades (ordinateurs portables, ordiphones, clés USB, disques durs externes, etc.) afin de préserver leur confidentialité. Seul un secret (mot de passe, carte à puce, code PIN, etc.) pourra permettre à celui qui le possède d’accéder à ces données.<br>Une solution de chiffrement de partition, d’archives ou de fichier peut être envisagée selon les besoins. Là encore, il est essentiel de s’assurer de l’unicité et de la robustesse du secret de déchiffrement utilisé. Dans la mesure du possible, il est conseillé de commencer par un chiffrement complet du disque avant d’envisager le chiffrement d’archives ou de fichiers.<br>En effet, c',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2692','32','Sécuriser la connexion réseau des postes utilisés en situation de nomadisme','/Standard<br>En situation de nomadisme, il n’est pas rare qu’un utilisateur ait besoin de se connecter au système d’information de l’entité. Il convient par conséquent de s’assurer du caractère sécurisé de cette connexion réseau à travers Internet.<br>Même si la possibilité d’établir des tunnels VPN SSL/TLS est aujourd’hui courante, il est fortement recommandé d’établir un tunnel VPN IPsec entre le poste nomade et une passerelle VPN IPsec mise à disposition par l’entité.<br>Pour garantir un niveau de sécurité optimal, ce tunnel VPN IPsec doit être automatiquement établi et ne pas être débrayable par l’utilisateur, c’est-à-dire qu’aucun flux ne doit pouvoir être transmis en dehors de ce tunnel.<br>Pour les besoins spécifiques d’authentification aux portails captifs, l’entité peut choisir de déroger à la connexion automatique en autorisant une connexion à la demande ou maintenir cette recommandation en encourageant l’utilisateur à utiliser un partage de connexion sur un téléphone mobile ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2693','33','Adopter des politiques de sécurité dédiées aux terminaux mobiles','/Standard<br>Les ordiphones et tablettes font partie de notre quotidien personnel et/ou professionnel. La première des recommandations consiste justement à ne pas mutualiser les usages personnel et professionnel sur un seul et même terminal, par exemple en ne synchronisant pas simultanément comptes professionnel et personnel de messagerie, de réseaux sociaux, d’agendas, etc.<br>Les terminaux, fournis par l’entité et utilisés en contexte professionnel doivent faire l’objet d’une sécurisation à part entière, dès lors qu’ils se connectent au système d’information de l’entité ou qu’ils contiennent des informations professionnelles potentiellement sensibles (mails, fichiers partagés, contacts, etc.). Dès lors, l’utilisation d’une solution de gestion centralisée des équipements mobiles est à privilégier. Il sera notamment souhaitable de configurer de manière homogène les politiques de sécurité inhérentes : moyen de déverrouillage du terminal, limitation de l’usage du magasin d’applications à',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2694','34','Définir une politique de mise à jour des composants du système d’information','/Standard<br>De nouvelles failles sont régulièrement découvertes au coeur des systèmes et logiciels. Ces dernières sont autant de portes d’accès qu’un attaquant peut exploiter pour réussir son intrusion dans le système d’information. Il est donc primordial de s’informer de l’apparition de nouvelles vulnérabilités (CERTFR) et d’appliquer les correctifs de sécurité sur l’ensemble des composants du système dans le mois qui suit leur publication par l’éditeur. Une politique de mise à jour doit ainsi être définie et déclinée en procédures opérationnelles.<br>Celles-ci doivent notamment préciser :<br>>> la manière dont l’inventaire des composants du système d’information est réalisé ;<br>>> les sources d’information relatives à la publication des mises à jour ;<br>>> les outils pour déployer les correctifs sur le parc (par exemple WSUS pour les mises à jour des composants Microsoft, des outils gratuits ou payants pour les composants tiers et autres systèmes d’exploitation) ;<br>>> l’éventuel',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2695','35','Anticiper la fin de la maintenance des logiciels et systèmes et limiter les adhérences logicielles','/Standard<br>L’utilisation d’un système ou d’un logiciel obsolète augmente significativement les possibilités d’attaque informatique. Les systèmes deviennent vulnérables dès lors que les correctifs ne sont plus proposés. En effet, des outils malveillants exploitant ces vulnérabilités peuvent se diffuser rapidement sur Internet alors même que l’éditeur ne propose pas de correctif de sécurité.<br>Pour anticiper ces obsolescences, un certain nombre de précautions existent :<br>>> établir et tenir à jour un inventaire des systèmes et applications du système d’information ;<br>>> choisir des solutions dont le support est assuré pour une durée correspondant à leur utilisation ;<br>>> assurer un suivi des mises à jour et des dates de fin de support des logiciels ;<br>>> maintenir un parc logiciel homogène (la coexistence de versions différentes d’un même produit multiplie les risques et complique le suivi) ;<br>>> limiter les adhérences logicielles, c’est-à-dire les dépendances de fonctionnem',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2696','36','Activer et configurer les journaux des composants les plus importants','/Standard<br>Disposer de journaux pertinents est nécessaire afin de pouvoir détecter d’éventuels dysfonctionnements et tentatives d’accès illicites aux composants du système d’information.<br>La première étape consiste à déterminer quels sont les composants critiques du système d’information. Il peut notamment s’agir des équipements réseau et de sécurité, des serveurs critiques, des postes de travail d’utilisateurs sensibles, etc.<br>Pour chacun, il convient d’analyser la configuration des éléments journalisés (format, fréquence de rotation des fichiers, taille maximale des fichiers journaux, catégories d’évènements enregistrés, etc.) et de l’adapter en conséquence.<br>Les évènements critiques pour la sécurité doivent être journalisés et gardés pendant au moins un an (ou plus en fonction des obligations légales du secteur d’activités).<br>Une étude contextuelle du système d’information doit être effectuée et les éléments suivants doivent être journalisés :<br>>> pare-feu : paquets bloq',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2697','37','Définir et appliquer une politique de sauvegarde des composants critiques','/Standard<br>Suite à un incident d’exploitation ou en contexte de gestion d’une intrusion, la disponibilité de sauvegardes conservées en lieu sûr est indispensable à la poursuite de l’activité. Il est donc fortement recommandé de formaliser une politique de sauvegarde régulièrement mise à jour. Cette dernière a pour objectif de définir des exigences en matière de sauvegarde de l’information, des logiciels et des systèmes.<br>Cette politique doit au moins intégrer les éléments suivants :<br>>> la liste des données jugées vitales pour l’organisme et les serveurs concernés ;<br>>> les différents types de sauvegarde (par exemple le mode hors ligne) ;<br>>> la fréquence des sauvegardes ;<br>>> la procédure d’administration et d’exécution des sauvegardes ;<br>>> les informations de stockage et les restrictions d’accès aux sauvegardes ;<br>>> les procédures de test de restauration ;<br>>> la destruction des supports ayant contenu les sauvegardes.<br>Les tests de restauration peuvent être réal',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2698','38','Procéder à des contrôles et audits de sécurité réguliers puis appliquer les actions correctives asso','/Renforcé<br>La réalisation d’audits réguliers (au moins une fois par an) du système d’information est essentielle car elle permet d’évaluer concrètement l’efficacité des mesures mises en oeuvre et leur maintien dans le temps. Ces contrôles et audits permettent également de mesurer les écarts pouvant persister entre la règle et la pratique.<br>Ils peuvent être réalisés par d’éventuelles équipes d’audit internes ou par des sociétés externes spécialisées. Selon le périmètre à contrôler, des audits techniques et/ou organisationnels seront effectués par les professionnels mobilisés. Ces audits sont d’autant plus nécessaires que l’entité doit être conforme à des réglementations et obligations légales directement liées à ses activités.<br>À l’issue de ces audits, des actions correctives doivent être identifiées, leur application planifiée et des points de suivi organisés à intervalles réguliers. Pour une plus grande efficacité, des indicateurs sur l’état d’avancement du plan d’action pourron',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2699','39','Désigner un référent en sécurité des systèmes d’information et le faire connaître auprès du personne','/Standard<br>Toute entité doit disposer d’un référent en sécurité des systèmes d’information qui sera soutenu par la direction ou par une instance décisionnelle spécialisée selon le niveau de maturité de la structure.<br>Ce référent devra être connu de tous les utilisateurs et sera le premier contact pour toutes les questions relatives à la sécurité des systèmes d’information :<br>>> définition des règles à appliquer selon le contexte ;<br>>> vérification de l’application des règles ;<br>>> sensibilisation des utilisateurs et définition d’un plan de formation des acteurs informatiques ;<br>>> centralisation et traitement des incidents de sécurité constatés ou remontés par les utilisateurs.<br>Ce référent devra être formé à la sécurité des systèmes d’information et à la gestion de crise.<br>Dans les entités les plus importantes, ce correspondant peut être désigné pour devenir le relais du RSSI. Il pourra par exemple signaler les doléances des utilisateurs et identifier les thématiques à',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2700','40','Définir une procédure de gestion des incidents de sécurité','/Standard<br>Le constat d’un comportement inhabituel de la part d’un poste de travail ou d’un serveur (connexion impossible, activité importante, activités inhabituelles, services ouverts non autorisés, fichiers créés, modifiés ou supprimés sans autorisation, multiples alertes de l’antivirus, etc.) peut alerter sur une éventuelle intrusion.<br>Une mauvaise réaction en cas d’incident de sécurité peut faire empirer la situation et empêcher de traiter correctement le problème. Le bon réflexe est de déconnecter la machine du réseau, pour stopper l’attaque. En revanche, il faut la maintenir sous tension et ne pas la redémarrer, pour ne pas perdre d’informations utiles pour l’analyse de l’attaque. Il faut ensuite prévenir la hiérarchie, ainsi que le référent en sécurité des systèmes d’information.<br>Celui-ci peut prendre contact avec un prestataire de réponse aux incidents de sécurité (PRIS) afin de faire réaliser les opérations techniques nécessaires (copie physique du disque, analyse de l',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2701','41','Mener une analyse de risques formelle','/Renforcé<br>Chaque entité évolue dans un environnement informationnel complexe qui lui est propre. Aussi, toute prise de position ou plan d’action impliquant la sécurité du système d’information doit être considéré à la lumière des risques pressentis par la direction. En effet, qu’il s’agisse de mesures organisationnelles ou techniques, leur mise en oeuvre représente un coût pour l’entité qui nécessite de s’assurer qu’elles permettent de réduire au bon niveau un risque identifié.<br>Dans les cas les plus sensibles, l’analyse de risque peut remettre en cause certains choix passés. Ce peut notamment être le cas si la probabilité d’apparition d’un événement et ses conséquences potentielles s’avèrent critiques pour l’entité et qu’il n’existe aucune action préventive pour le maîtriser.<br>La démarche recommandée consiste, dans les grandes lignes, à définir le contexte, apprécier les risques et les traiter. L’évaluation de ces risques s’opère généralement selon deux axes : leur probabilité ',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2702','42','Privilégier l’usage de produits et de services qualifiés par l’ANSSI','/Renforcé<br>La qualification prononcée par l’ANSSI offre des garanties de sécurité et de confiance aux acheteurs de solutions listées dans les catalogues de produits et de prestataires de service qualifiés que publie l’agence.<br>Au-delà des entités soumises à réglementation, l’ANSSI encourage plus généralement l’ensemble des entreprises et administrations françaises à utiliser des produits qu’elle qualifie, seul gage d’une étude sérieuse et approfondie du fonctionnement technique de la solution et de son écosystème.<br>S’agissant des prestataires de service qualifiés, ce label permet de répondre aux enjeux et projets de cybersécurité pour l’ensemble du tissu économique français que l’ANSSI ne saurait adresser seule. Évalués sur des critères techniques et organisationnels, les prestataires qualifiés couvrent l’essentiel des métiers de la sécurité des systèmes d’information. Ainsi, en fonction de ses besoins et du maillage national, une entité pourra faire appel à un Prestataire d’audi',NULL,NULL,NULL,NULL,'20','26','1.d');
INSERT INTO `O_regle` VALUES ('2709','1','Former les équipes opérationnelles à la sécurité des systèmes d’information','/ standard<BR />Les équipes opérationnelles (administrateurs réseau, sécurité et système, chefs de projet, développeurs, RSSI) ont des accès privilégiés au système d’information. Elles peuvent, par inadvertance ou par méconnaissance des conséquences de certaines pratiques, réaliser des opérations génératrices de vulnérabilités. <BR />Citons par exemple l’affectation de comptes disposant de trop nombreux privilèges par rapport à la tâche à réaliser, l’utilisation de comptes personnels pour exécuter des services ou tâches périodiques, ou encore le choix de mots de passe peu robustes donnant accès à des comptes privilégiés.<BR />Les équipes opérationnelles, pour être à l’état de l’art de la sécurité des systèmes d’information, doivent donc suivre - à leur prise de poste puis à intervalles réguliers - des formations sur : <UL> <LI> la législation en vigueur ;</LI><LI> les principaux risques et menaces ;</LI><UL>',NULL,NULL,NULL,NULL,'21','26','1.d');
INSERT INTO `O_regle` VALUES ('2710','2','Sensibiliser les utilisateurs aux bonnes pratiques élémentaires de sécurité informatique','/ standard<BR /> Ligne 2',NULL,NULL,NULL,NULL,'21','26','1.d');
INSERT INTO `O_regle` VALUES ('2711','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2712','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2713','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2714','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2715','RÈGLE — Utiliser des options de durcissement','5','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2716','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2717','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2718','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2719','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2720','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2721','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2722','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2723','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2724','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2725','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2726','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2727','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2728','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2729','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2730','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2731','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2732','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2733','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2734','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2735','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2736','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2737','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2738','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2739','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2740','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2741','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2742','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2743','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2744','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2745','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2746','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2747','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2748','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2749','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2750','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2751','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2752','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2753','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2754','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2755','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2756','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2757','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2758','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2759','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2760','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2761','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2762','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2763','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2764','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2765','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2766','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2767','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2768','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2769','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2770','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2771','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2772','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2773','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2774','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2775','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2776','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2777','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2778','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2779','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2780','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2781','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2782','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2783','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2784','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2785','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2786','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2787','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2788','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2789','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2790','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2791','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2792','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2793','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2794','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2795','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2796','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2797','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2798','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2799','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2800','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2801','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2802','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2803','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2804','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2805','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2806','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2807','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2808','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2809','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2810','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2811','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2812','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2813','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2814','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2815','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2816','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2817','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2818','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2819','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2820','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2821','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2822','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2823','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2824','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2825','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2826','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2827','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2828','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2829','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2830','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2831','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2832','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2833','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2834','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2835','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2836','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2837','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2838','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2839','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2840','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2841','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2842','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2843','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2844','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2845','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2846','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2847','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2848','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2849','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2850','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2851','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2852','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2853','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2854','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2855','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2856','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2857','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2858','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2859','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2860','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2861','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2862','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2863','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2864','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2865','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2866','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2867','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2868','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2869','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2870','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2871','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2872','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2873','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2874','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2875','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2876','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2877','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2878','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2879','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2880','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2881','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2882','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2883','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2884','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2885','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2886','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2887','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2888','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2889','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2890','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2891','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'22','25','1.d');
INSERT INTO `O_regle` VALUES ('2892','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2893','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2894','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2895','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2896','RÈGLE — Utiliser des options de durcissement','5','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2897','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2898','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2899','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2900','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2901','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2902','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2903','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2904','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2905','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2906','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2907','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2908','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2909','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2910','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2911','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2912','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2913','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2914','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2915','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2916','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2917','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2918','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2919','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2920','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2921','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2922','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2923','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2924','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2925','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2926','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2927','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2928','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2929','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2930','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2931','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2932','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2933','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2934','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2935','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2936','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2937','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2938','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2939','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2940','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2941','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2942','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2943','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2944','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2945','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2946','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2947','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2948','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2949','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2950','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2951','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2952','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2953','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2954','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2955','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2956','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2957','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2958','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2959','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2960','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2961','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2962','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2963','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2964','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2965','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2966','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2967','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2968','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2969','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2970','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2971','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2972','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2973','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2974','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2975','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2976','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2977','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2978','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2979','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2980','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2981','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2982','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2983','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2984','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2985','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2986','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2987','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2988','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2989','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2990','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2991','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2992','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2993','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2994','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2995','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2996','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2997','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2998','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('2999','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3000','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3001','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3002','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3003','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3004','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3005','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3006','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3007','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3008','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3009','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3010','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3011','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3012','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3013','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3014','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3015','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3016','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3017','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3018','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3019','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3020','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3021','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3022','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3023','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3024','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3025','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3026','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3027','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3028','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3029','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3030','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3031','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3032','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3033','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3034','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3035','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3036','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3037','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3038','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3039','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3040','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3041','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3042','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3043','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3044','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3045','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3046','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3047','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3048','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3049','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3050','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3051','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3052','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3053','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3054','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3055','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3056','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3057','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3058','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3059','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3060','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3061','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3062','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3063','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3064','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3065','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3066','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3067','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3068','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3069','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3070','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3071','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3072','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3073','1','RÈGLE — Application de conventions de codage claires et explicites','Des conventions de codage doivent être définies et documentées pour le logiciel à produire. Ces conventions doivent définir au minimum les points suivants : l’encodage des fichiers sources, la mise en page du code et l’indentation, les types standards à utiliser, le nommage (bibliothèques, fichiers, fonctions, types, variables, . . .), le format de la documentation. Ces conventions doivent être appliquées par chaque développeur.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3074','2','RÈGLE — Seul le codage C conforme au standard est autorisé','Aucune violation des contraintes et de la syntaxe C telles que définies dans les standards C90 ou C99 n’est autorisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3075','3','RECOMMANDATION — Maîtrise des actions opérées à la compilation.',' Le développeur doit connaître et documenter les actions associées aux options activées du compilateur y compris en terme d’optimisation de code',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3076','4','RÈGLE — Définir précisément les options de compilation','Les options utilisées pour la compilation doivent être précisément définies pour l’ensemble des sources d’un logiciel. Ces options doivent notamment fixer précisément :\n- la version du standard C utilisée (par exemple C99 ou encore C90) ;\n- le nom et la version du compilateur utilisé ;',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3077','RÈGLE — Utiliser des options de durcissement','5','L’utilisation d’options de durcissement est obligatoire que ce soit pour imposer la génération d’exécutables relocalisables, une randomization d’adresses efficace ou la protection contre le dépassement de pile entre autres.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3078','6','BONNE PRATIQUE — Utiliser des générateurs de projets pour la compilation.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3079','7','RÈGLE — Compiler le code sans erreur ni avertissement en activant des options de compilation exigent','Activer le niveau d’avertissement et d’erreur le plus élevé du compilateur et de l’éditeur de liens afin de s’assurer de l’absence de problèmes potentiels liés à l’utilisation incorrecte du langage de programmation et traiter tous les avertissements et toutes les erreurs signalés par le compilateur et l’éditeur de liens pour les éliminer.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3080','8','RECOMMANDATION — Utiliser les options des compilations les plus exigentes','Si une option élevée d’un compilateur n’apparaît pas pertinente pour un développement donné, une justification sera fournie pour expliquer ce choix.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3081','9','RÈGLE — Tout code mis en production doit être compilé en mode release','La compilation en mode release est obligatoire pour une mise en production.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3082','10','RECOMMANDATION — Prêter une attention particulière aux modes debug et release lors de la compilation','L’utilisation des modes debug et release à la compilation doit se faire en toute connaissance des modifications induites en terme de gestion de mémoire et d’optimisation de code. Les différences entre ces deux modes doivent être documentées de manière exhaustive.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3083','11','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3084','12','RECOMMANDATION — Limiter et justifier les inclusions de fichier d''en-tête dans un autre fichier d''en','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3085','13','RÈGLE — Utiliser des macros de garde d''inclusion multiple d''un fichier','Une macro de garde contre l’inclusion multiple d’un fichier doit être utilisée afin d’empêcher que le contenu d’un fichier d’en-tête soit inclus plus d’une fois :\n// début du fichier d''en -tête\n#ifndef HEADER_H\n#define HEADER_H\n/* contenu du fichier */\n#endif\n//fin du fichier d''en -tête',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3086','14','RÈGLE — Les inclusions de fichiers d''en-tête sont groupées en début de fichier','Toutes les inclusions de fichiers d’en-tête doivent être regroupées au début du fichier ou juste après des commentaires ou les directives de preprocessing, mais systématiquement avant la définition de variables globales ou de fonctions.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3087','15','RECOMMANDATION — Les inclusions de fichiers d''en-tête systèmes sont effectuées avant les inclusions ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3088','16','BONNE PRATIQUE — Utiliser l''ordre alphabétique dans l''inclusion de chaque type de fichiers d''en-tête','Pour éviter les redondances dans les inclusions de fichiers d’en-tête systèmes ou utilisateur, le développeur peut les ordonner par ordre alphabétique ce qui permet d’avoir un ordre d’inclusion déterministe et de faciliter la revue de code.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3089','17','RÈGLE — Ne pas inclure un fichier source dans un autre fichier source','Seule l’inclusion de fichiers d’en-tête est autorisée dans un fichier source.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3090','18','RÈGLE — Les chemins des fichiers doivent être portables et la casse doit être respectée','Les chemins de fichiers, que ce soit pour une directive d’inclusion #include ou non, doivent être portables tout en respectant la casse des répertoires.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3091','19','RÈGLE — Le nom d''un fichier d''en-tête ne doit pas contenir certains caractères ou séquences de carac','Le nom d’un fichier d’en-tête doit être exempt des caractères et des séquences de caractères suivants : « '', ", \\, /* et // ».',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3092','20','RECOMMANDATION — Les blocs préprocesseurs doivent être commentés','Les directives des blocs préprocesseurs doivent être commentées afin d’expliciter le cas traité et pour les directives « intermédiaires » et « fermantes », celles-ci doivent aussi être associées à la directive « ouvrante » correspondante par le biais d’un commentaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3093','21','BONNE PRATIQUE — La double négation dans l''expression des conditions des blocs préprocesseurs doit ê','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3094','22','RÈGLE — Définition d''un bloc préprocesseur dans un seul et même fichier','Pour un bloc préprocesseur, toutes les directives associées doivent se trouver dans le même fichier.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3095','23','RECOMMANDATION — Les expressions de contrôle des directives de preprocessing doivent être bien formé','Les expressions de contrôle doivent être évaluées uniquement à 0 ou 1 et doivent utiliser uniquement des identifiants définis (via #define).',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3096','24','RÈGLE — Ne pas utiliser dans une même expression plus d''un des opérateurs de preprocessing # et ##','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3097','25','RÈGLE — Utiliser les opérateurs de preprocessing # et ## en maîtrisant leur expansion','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3098','26','RÈGLE — Les macros doivent être nommées de façon spécifique','Pour différencier aisément les macros des fonctions et ne pas utiliser un nom réservé d’une autre macro C, les macros préprocesseurs doivent être en capitales et les mots composant le nom séparés par le caractère souligné « _ » mais sans les faire débuter par le caractère souligné car il s’agit d’une convention pour les noms réservés du langage C.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3099','27','RÈGLE — Ne pas terminer une macro par un point-virgule','Le point-virgule final doit être omis à la fin de la définition d’une macro.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3100','28','Le point-virgule final doit être omis à la fin de la définition d’une macro.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3101','29','RÈGLE — L''expansion d''une macro définie par le développeur ne doit pas créer de fonction','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3102','30','RÈGLE — Les macros contenant plusieurs instructions doivent utiliser une boucle do { ... } while(0) ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3103','31','RÈGLE — Parenthèses obligatoires autour des paramètres utilisés dans le corps d''une macro','Les paramètres d’une macro doivent systématiquement être entourés de parenthèses lors de leur utilisation, afin de préserver l’ordre souhaité d’évaluation des expressions.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3104','32','RECOMMANDATION — Il faut éviter les arguments d''une macro réalisant une opération','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3105','33','RÈGLE — Les arguments d''une macro ne doivent pas contenir d''effets de bord.','Des arguments de macro avec des effets de bord peuvent entraîner des évaluations multiples non désirées.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3106','34','RÈGLE — Ne pas utiliser de directives de preprocessing en arguments d''une macro','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3107','35','RÈGLE — La directive #undef ne doit pas être utilisée','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3108','36','RÈGLE — Ne pas utiliser de trigraphes','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3109','37','RECOMMANDATION — Les points d''interrogation ne doivent pas être utilisés de façon successive','Cette règle s’applique pour toute partie du code mais aussi pour les commentaires.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3110','38','RECOMMANDATION — Seules les déclarations multiples de variables simples de même type sont autorisées','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3111','39','RÈGLE — Ne pas faire de déclaration multiple de variables associée à une initialisation.','Les initialisations associées (i.e. consécutives et dans une même instruction) à une déclaration multiple sont interdites.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3112','40','RECOMMANDATION — Regrouper les déclarations de variables en début du bloc dans lequel elles sont uti','Pour des questions de lisibilité et pour éviter les redéfinitions, les déclarations de variables sont regroupées en début de fichier, fonction ou bloc d’instructions selon leur portée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3113','41','RÈGLE — Ne pas utiliser des valeurs en dur','Les valeurs utilisées dans le code doivent être déclarées comme des constantes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3114','42','BONNE PRATIQUE — Centraliser la déclaration des constantes en début de fichier','Pour faciliter la lecture, les constantes sont déclarées ensemble en début du fichier.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3115','43','RÈGLE — Déclarer les constantes en capitales','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3116','44','RÈGLE — Les constantes sans contrôle de type sont déclarées avec la macro préprocesseur de définitio','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3117','45','RÈGLE — Les constantes avec un contrôle de type explicite doivent être déclarées avec le mot clé con','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3118','46','RÈGLE — Les valeurs constantes doivent être associées à un suffixe dépendant du type','Pour éviter toute mauvaise interprétation, les valeurs constantes doivent utiliser un suffixe selon leurs types :\n il faut utiliser le suffixe U pour toutes les constantes de type entier non signé ; \n pour indiquer une constante de type long (ou long long pour C99), il faut utiliser le suffixe L (resp. LL) et non l (resp. ll) afin d’éviter toute ambiguïté avec le chiffre 1 ;\n les valeurs flottantes sont par défaut considérées comme double, il faut utiliser le suffixe f pour le type float (resp. d pour le type double).',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3119','47','RÈGLE — La taille du type associé à une expression constante doit être suffisante pour la contenir','Il faut s’assurer que les valeurs ou expressions constantes utilisées ne dépassent pas du type qui leur est associé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3120','48','RECOMMANDATION — Proscrire les constantes en octal','Ne pas utiliser de constante ni de séquence d’échappement en octal.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3121','49','RÈGLE — Limiter les variables globales au strict nécessaire','Limiter l’usage des variables globales et préférer les paramètres de fonctions pour propager une structure de données au travers d’une application.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3122','50','RÈGLE — Utilisation systématique du modificateur de déclaration static','Utiliser le mot clef static pour toutes les fonctions et variables globales qui ne sont pas utilisées à l’extérieur du fichier source dans lequel elles sont définies.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3123','51','RÈGLE — Seules les variables modifiables en dehors de l''implémentation doivent être déclarées volati','Seules les variables associées à des ports entrée/sortie ou des fonctions d’interruption asynchrone doivent être déclarées comme volatile pour empêcher toute optimisation ou réorganisation à la compilation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3124','52','RÈGLE — Seuls des pointeurs spécifiés volatile peuvent accéder à des variables volatile','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3125','53','RÈGLE — Aucune omission de type n''est acceptée lors de la déclaration d''une variable','Toutes les variables utilisées doivent avoir été préalablement déclarées de façon explicite.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3126','54','RECOMMANDATION — Limiter l''utilisation des compound literals','Du fait du risque de mauvaise manipulation des compound literals, leur utilisation doit être limitée, documentée et une attention particulière doit être donnée à leur portée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3127','55','RÈGLE — Ne pas mélanger des constantes explicites et implicites dans une énumération','Il faut soit expliciter toutes les constantes d’une énumération avec une valeur unique soit n’en expliciter aucune.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3128','56','RÈGLE — Ne pas utiliser des énumérations anonymes','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3129','57','RECOMMANDATION — Les variables doivent être initialisées à la déclaration ou immédiatement après','Toutes les variables doivent être systématiquement initialisées à leur déclaration ou immédiatement après dans le cas de déclarations multiples.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3130','58','RÈGLE — Ne pas mélanger les différents types d''initialisation pour les variables structurées','Pour l’initialisation d’une variable structurée, un seul et unique type d’initialisation doit être choisi et utilisé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3131','59','RÈGLE — Les variables structurées ne doivent pas être initialisées sans expliciter la valeur d''initi','Les variables non scalaires doivent être initialisées explicitement : chaque élément doit être initialisé en étant clairement identifié sans valeur d’initialisation superflue ou la notation ={0} ; peut être utilisée à la déclaration. Enfin les tailles des tableaux doivent être explicitées à l’initialisation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3132','60','RECOMMANDATION — Chaque déclaration publique (non static) doit être utilisée','Toutes les déclarations publiques (i.e. non static) doivent être utilisées, qu’il s’agissede variables, fonctions, labels ou autres.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3133','61','RÈGLE — Utiliser des variables pour les données sensibles distinctes des variables pour les données ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3134','62','RÈGLE — Utiliser des variables pour les données sensibles et protégées en confidentialité et/ou inté','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3135','63','RÈGLE — Ne jamais coder en dur une donnée sensible.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3136','64','RECOMMANDATION — Seuls des types d''entiers dont la taille et le signe sont explicites doivent être u','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3137','65','RÈGLE — Seuls les types signed char et unsigned char doivent être utilisés.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3138','66','RECOMMANDATION — Ne pas redéfinir des alias de types','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3139','67','RÈGLE — Compréhension fine et précise des règles de conversions','Le développeur se doit de connaître et comprendre toutes les règles de conversionimplicites des types entiers.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3140','68','RÈGLE — Conversions explicites entre des types signés et non signés','Proscrire les conversions implicites de types. Utiliser des conversions explicites notamment entre type signé et type non signé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3141','69','RECOMMANDATION — Ne pas utiliser de transtypage de pointeurs sur des types structurés différents','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3142','70','RÈGLE — L''accès aux éléments d''un tableau se fera toujours en désignant en premier attribut le table','L’accès au ième élément d’un tableau s’écrira toujours avec le nom du tableau en premier suivi de l’indice de la case à atteindre.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3143','71','RECOMMANDATION — L''accès aux éléments d''un tableau doit se faire en utilisant les crochets','Dans le cas d’une variable de type tableau, la notation dédiée (via les crochets) doit être utilisée pour éviter toute ambiguïté.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3144','72','RÈGLE — Ne pas utiliser de VLA','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3145','73','RECOMMANDATION — Ne pas utiliser de taille implicite pour les tableaux','Afin de s’assurer que les accès tableaux sont bien valides, la taille de ceux-ci doit être explicitée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3146','74','RÈGLE — Utiliser des entiers non signés pour les tailles de tableaux','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3147','75','RÈGLE — Ne pas accèder à un élément de tableau sans vérifier la validité de l''indice utilisé','La validité des indices de tableau utilisés doit être vérifié de façon systématique : un indice de tableau est valide s’il est supérieur ou égal à zéro et strictement inférieur à la taille déclarée du tableau. Dans le cas d’un tableau de caractères, le caractère de fin de chaine ’\\0’ doit être pris en compte.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3148','76','RÈGLE — Un pointeur NULL ne doit pas être déréférencé','Avant de déréférencer un pointeur, le développeur doit s’assurer que celui-ci n’est pas NULL.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3149','77','RÈGLE — Un pointeur doit être affecté à NULL après désallocation','Un pointeur doit être systématiquement affecté à NULL suite à la désallocation de la mémoire qu’il pointe.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3150','78','RÈGLE — Ne pas utiliser le qualificateur de pointeur restrict','Le qualificateur restrict ne doit pas être utilisé directement par le développeur. Seule l’utilisation indirecte i.e. via l’appel de fonctions de la bibliothèque standard est tolérée mais le développeur devra s’assurer qu’aucun comportement indéfini résultera de l’utilisation de telles fonctions.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3151','79','RECOMMANDATION — Le nombre de niveau d''indirections de pointeur doit être limité à deux','Le nombre d’indirections pour un pointeur ne doit pas dépasser deux niveaux.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3152','80','RECOMMANDATION — Préférer l''utilisation de l''opérateur d''indirection ->','L’opérateur d’indirection -> doit être utilisé pour atteindre les champs d’une structure par l’intermédiaire d’un pointeur.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3153','81','RÈGLE — Seul l''incrément ou le décrément de pointeurs de tableaux est autorisé','L’incrément ou le décrément de pointeurs ne doit être utilisé que sur des pointeurs représentant un tableau ou un élément d’un tableau.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3154','82','RÈGLE — Aucune arithmétique sur les pointeurs void* n''est autorisée','Il faut proscrire l’utilisation de toute arithmétique sur des pointeurs de type void*.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3155','83','RECOMMANDATION — Arithmétique des pointeurs sur tableaux contrôlée','L’arithmétique sur des pointeurs représentant un tableau ou un élément d’un tableau doit être faite en s’assurant que le pointeur résultant pointera toujours sur un élément du même tableau.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3156','84','RÈGLE — Soustraction et comparaison entre pointeurs d''un même tableau uniquement','Seules les soustractions et comparaisons de pointeurs sur un même tableau sont autorisés.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3157','85','RECOMMANDATION — Il ne faut pas affecter directement une adresse fixe à un pointeur.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3158','86','RÈGLE — Une structure doit être utilisée pour regrouper les données représentant une même entité','Les données liées doivent être regroupées au sein d’une structure.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3159','87','RÈGLE — Ne pas calculer la taille d''une structure comme la somme de la taille de ses champs','Du fait du padding, la taille d’une structure ne doit pas être supposée comme la somme de la taille de ses champs.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3160','88','RÈGLE — Tout bitfield doit obligatoirement être déclaré explicitement comme non signé','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3161','89','RÈGLE — Ne pas faire d''hypothèse sur la représentation interne de structures avec des bitfields','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3162','90','RÈGLE — Ne pas utiliser les FAM','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3163','91','RECOMMANDATION — Ne pas utiliser les unions','L’utilisation du même espace mémoire pour plusieurs données de natures différentes n’est pas autorisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3164','92','RÈGLE — Supprimer tous les débordements de valeurs possibles pour des entiers signés.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3165','93','RECOMMANDATION — Détecter tous les wraps possibles de valeurs pour les entiers non signés.','vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3166','94','RÈGLE — Détecter et supprimer toute potentielle division par zéro','Cette vérification doit être systématique pour tout calcul de division ou de reste de division.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3167','95','RECOMMANDATION — Les opérations arithmétiques doivent être écrites en favorisant leur lisibilité','Il faut utiliser des opérations arithmétiques le plus explicites possibles (naturelles) et dans la logique du programme.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3168','96','RECOMMANDATION — Les opérateurs logiques ne doivent pas être appliqués avec des opérandes signés','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3169','97','RÈGLE — Explicitation de l''ordre d''évaluation des calculs par utilisation de parenthèses','Malgré la priorité des opérateurs, pour éviter toute ambiguïté, les expressions seront entourées de parenthèses pour rendre plus explicite l’ordre d’évaluation d’un calcul.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3170','98','RECOMMANDATION — Eviter les expressions de comparaison ou d''égalité multiple','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3171','99','RÈGLE — Toujours utiliser les parenthèses dans les expressions de comparaison ou d''égalité multiple','Les expressions booléennes de comparaison ou d’égalité contenant au moins 2 opérateurs relationnels sont interdites sans parenthèse.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3172','100','RÈGLE — Parenthèses autour des éléments d''une expression booléenne','Il est nécessaire de toujours mettre entre parenthèses les différents éléments d’une expression booléenne, afin qu’il n’y ait aucune ambiguïté dans l’ordre d’évaluation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3173','101','RÈGLE — Comparaison implicite avec 0 interdite','Toutes les expressions booléennes doivent utiliser des opérateurs de comparaison. Aucun test implicite avec une valeur égale à 0 ou différente de 0 ne doit être effectué.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3174','102','RECOMMANDATION — Utilisation du type bool en C99','En C99, le type bool (ou _Bool) doit être utilisé pour les variables à valeurs booléennes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3175','103','RÈGLE — Pas d''opérateur bit-à-bit sur un opérande de type booléen ou assimilé','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3176','104','BONNE PRATIQUE — Ne pas utiliser la valeur retournée lors d''une affectation','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3177','105','RÈGLE — Affectation interdite dans une expression booléenne','Une affectation ne doit pas être effectuée dans une expression booléenne quelle qu’elle soit. Une affectation doit être effectuée dans une instruction indépendante.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3178','106','BONNE PRATIQUE — Comparaison avec opérande constant à gauche','Quand une comparaison fait intervenir un opérande constant celui-ci sera de préférence mis comme opérande gauche pour éviter une affectation non intentionnelle.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3179','107','RÈGLE — Affectation multiple de variables interdite','L’affectation multiple de variables n’est pas autorisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3180','108','RÈGLE — Une seule instruction par ligne de code','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3181','108','BONNE PRATIQUE — Éviter les constantes flottantes','Ne pas utiliser de constantes numériques flottantes pour éviter les pertes de précision et autres phénomènes liés aux nombres flottants. Si cela ne peut être évité, la représentativité de la valeur flottante en question doit être vérifiée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3182','110','RECOMMANDATION — Limiter l''utilisation des nombres flottants au strict nécessaire','Il faut limiter l’utilisation des nombres flottants.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3183','111','RÈGLE — Pas de compteur de boucle de type flottant','Les compteurs de boucle doivent être uniquement de type entier, avec la vérification de non débordement de type des valeurs des compteurs.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3184','112','RÈGLE — Ne pas utiliser de nombres flottants pour des comparaisons d''égalité ou d''inégalité','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3185','113','RECOMMANDATION — Non utilisation des nombres complexes','Les nombres complexes introduits depuis C99 ne doivent pas être utilisés.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3186','114','RÈGLE — Utilisation systématique des accolades pour les conditionnelles et les boucles','Ne jamais omettre les accolades pour délimiter un bloc d’instructions. Les accolades doivent être écrites pour délimiter un bloc d’instructions après les boucles (for, while, do) et les conditionnelles (if, else).',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3187','115','RÈGLE — Définition systématique d''un cas par défaut dans les switch','Un switch-case doit toujours contenir un cas default placé en dernier.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3188','116','RECOMMANDATION — Utilisation de break dans chaque cas des instructions switch','Un switch-case doit par défaut toujours contenir un break pour chaque cas. L’absence de break pour éviter de dupliquer du code est tolérée mais doit être explicitée dans un commentaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3189','117','RECOMMANDATION — Pas d''imbrication de structure de contrôle dans un switch-case',' Même si le C l’autorise, l’imbrication de structures de contrôle à l’intérieur d’un switch est à éviter.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3190','118','RÈGLE — Ne pas introduire d''instructions avant le premier label d''un switch-case','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3191','119','RÈGLE — Bonne construction des boucles for','Chaque élément d’une boucle for doit être complété et contenir exactement une seule instruction. Ainsi une boucle for doit contenir une initialisation de son compteur, une condition d’arrêt portant sur son compteur, et une incrémentation ou décrémentation du compteur de boucle.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3192','120','RÈGLE — Modification d''un compteur d''une boucle for interdite dans le corps de la boucle','Le compteur d’une boucle for ne doit pas être modifié à l’intérieur du corps de la boucle for.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3193','121','RÈGLE — Non utilisation de goto arrière (backward goto)','Proscrire, au sein d’une fonction, l’utilisation d’instructions goto renvoyant vers un label qui est placé avant cette instruction goto.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3194','122','RECOMMANDATION — Utilisation limitée du saut avant (forward goto)','L’utilisation d’un forward goto est tolérée uniquement dans les cas où elle permet :\n de limiter significativement le nombre de points de sortie de la fonction ;\n de rendre le code beaucoup plus lisible.\nLe ou les labels référencés par les instructions goto doivent tous être situés en fin de fonction.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3195','123','RÈGLE — Toute fonction (non static) définie doit possèder une déclaration/ prototype de fonction','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3196','124','RÈGLE — Le prototype de déclaration d''une fonction doit concorder avec sa définition','Les types des paramètres utilisés pour la définition et la déclaration d’une fonction doivent être les mêmes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3197','125','RÈGLE — Toute fonction doit être associée à un type de retour et à une liste de paramètres explicite','Chaque fonction est définie explicitement avec un type de retour. Les fonctions sans valeur de retour doivent être déclarées avec un paramètre du type void. De la même façon, une fonction sans paramètre devra être définie et déclarée avec void en argument.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3198','126','RECOMMANDATION — Documentation des fonctions','Tous les fonctions doivent être documentées. Cela comprend :\nn une description de la fonction et du traitement effectué ;\n la documentation de chaque paramètre, le sens du paramètre (en entrée, en sortie, en entrée et en sortie) et les éventuelles conditions existant sur celui-ci ;\n les valeurs de retour possibles doivent être décrites.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3199','127','RECOMMANDATION — Préciser les conditions d''appel pour chaque fonction','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3200','128','RÈGLE — La validité de tous les paramètres d''une fonction doit systématiquement être remise en cause','Cela inclut :\n la validité des adresses pour les paramètres de type pointeur doit être vérifiée (non , alignement des adresses conforme...) ;\n l’appartenance des paramètres à leur domaine doit être vérifiée.\nCela s’applique aux fonctions définies par le développeur (cf. section 12.2) mais aussi aux fonctions de la bibliothèque standard.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3201','129','RÈGLE — Les paramètres de fonction de type pointeur pour lesquels la zone mémoire pointée n''est pas ','Marquer const tous les paramètres de type pointeur d’une fonction qui pointent vers une zone mémoire qui ne doit pas être modifiée dans le corps de celle-ci. Le qualificateur const doit s’appliquer à l’objet pointé.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3202','130','RÈGLE — Les fonctions inline doivent être déclarées comme static','Pour éviter un comportement non défini une fonction inline est systématiquement static.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3203','131','RÈGLE — Interdiction de redéfinir les fonctions ou macros de la bibliothèque standard ou d''une autre','Les identifiants, macros ou noms de fonctions faisant partie de la bibliothèque standard ou d’une autre bibliothèque utilisée ne doivent pas être redéfinis.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3204','132','RÈGLE — La valeur de retour d''une fonction doit toujours être testée','Lorsqu’une fonction retourne une valeur, la valeur retournée doit être systématiquement testée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3205','133','RÈGLE — Retour implicite interdit pour les fonctions de type non void','Tous les chemins d’une fonction non void doivent retourner une valeur explicitement.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3206','134','RÈGLE — Les structures doivent être passées par référence à une fonction','Il ne faut pas passer de paramètres de type structure par copie lors de l’appel d’une fonction.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3207','135','RECOMMANDATION — Passage d''un tableau en paramètre d''une fonction','Il existe plusieurs façons de passer un tableau en paramètre d’une fonction. Lors du passage par pointeur, il faut préciser dans la documentation de la fonction que le paramètre correspond à un tableau et également utiliser la notation dédiée aux tableaux.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3208','136','RECOMMANDATION — Utilisation obligatoire dans une fonction de tous ses paramètres','Tous les paramètres présents dans le prototype de la fonction doivent être utilisés dans son implémentation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3209','137','BONNE PRATIQUE — Utiliser les options de compilation -Wformat=2 et -Wformat-security dès qu''une fonc','Plus de détails sur ces options sont données en annexe B.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3210','138','RÈGLE — Ne pas appeler de fonctions variadiques avec NULL en argument','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3211','139','RÈGLE — Usage de la virgule interdit pour le séquencement d''instructions','La virgule n’est pas autorisée dans le cadre du séquencement des instructions decode.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3212','140','RECOMMANDATION — Les opérateurs pré-fixes ++ et -- ne doivent pas être utilisés','Les opérateurs de pré-incrémentation et pré-decrémentation ne seront pas utilisés.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3213','141','RECOMMANDATION — Pas d''utilisation combinée des opérateurs postfixes avec d''autres opérateurs','Les opérateurs de post-incrémentation et de post-décrémentation ne doivent pas être mixés avec d’autres opérateurs.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3214','142','RECOMMANDATION — Éviter l''utilisation d''opérateurs d''affectation combinés','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3215','143','RÈGLE — Non utilisation imbriquée de l''opérateur ternaire ?:','L’imbrication d’opérateurs ternaires ?: est interdite.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3216','144','RÈGLE — Bonne construction des expressions avec l''opérateur ternaire ?:','Les expressions résultantes de l’opérateur ternaire ?: doivent être exactement de même type pour éviter tout transtypage.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3217','145','RÈGLE — Allouer dynamiquement un espace mémoire dont la taille est suffisante pour l''objet alloué','Pour un pointeur ptr, on préférera utiliser ptr=malloc(sizeof(*ptr)); quand cela est possible.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3218','146','RÈGLE — Libérer la mémoire allouée dynamiquement au plus tôt','Tout espace mémoire alloué dynamiquement doit être libéré quand celui-ci n’est plus utile.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3219','147','RÈGLE — Les zones mémoires sensibles doivent être mises à zéro avant d''être libérées.','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3220','148','RÈGLE — Ne pas libérer de mémoire non allouée dynamiquement','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3221','149','RÈGLE — Ne pas modifier l''allocation dynamique via realloc','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3222','150','RÈGLE — Bonne utilisation de l''opérateur sizeof','Une expression contenue dans un sizeof ne doit pas :\n contenir l’opérateur « = » car l’expression ne sera pas évaluée ; \n contenir de déréférencement de pointeur ;\n être appliqué sur un pointeur représentant un tableau.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3223','151','RÈGLE — Vérification obligatoire du succès d''une allocation mémoire','Le succès d’une allocation mémoire doit toujours être vérifié.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3224','152','RÈGLE — L''isolement des données sensibles doit être effectué','Contrôler le bon usage d’une zone mémoire stockant des données sensibles i.e. minimiser l’exposition en mémoire, minimiser la copie et effacer la/les zones ayant contenu les données sensibles au plus tôt.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3225','153','RÈGLE — Initialiser et consulter la valeur de errno avant et après toute exécution d''une fonction de','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3226','154','RÈGLE — La gestion des erreurs retournées par une fonction de la bibliothèque standard doit être sys','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3227','155','RÈGLE — Documentation des codes d''erreur','Tous les codes d’erreur retournés par une fonction doivent être documentés. Dans le cas où plusieurs codes d’erreur peuvent être retournés en même temps par la fonction, la documentation doit définir la priorité de gestion de ces codes.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3228','156','RECOMMANDATION — Structuration des codes de retour','Les codes de retour doivent être structurés de façon à pouvoir obtenir rapidement une information concernant le déroulement de la fonction appelée : \n erreur ; \n type d’erreur ; \n alarme ; \n type d’alarme ; \n ok ;',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3229','157','RÈGLE — Code de retour d''un programme C en fonction du résultat de son exécution','Le code de retour d’un programme C doit avoir une signification afin d’indiquer le bon déroulement du programme ou la survenue d’une erreur : \n la valeur du code de retour doit être comprise entre 0 et 127 ; \n la valeur 0 indique que le programme s’est exécuté sans erreur ;\n la valeur 2 est généralement utilisée sous Unix pour indiquer une erreur dans les arguments passés en paramètres au programme. \nLa signification des codes de retour du programme doit être indiquée dans sa documentation.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3230','158','RECOMMANDATION — Privilégier les retours d''erreurs via des codes de retour dans la fonction principa','Un programme C doit disposer d’une fonction main() minimale. Les retours d’erreurs se font par un retour de code dédié (et donc documenté) de cette fonction.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3231','159','RÈGLE — Ne pas utiliser les fonctions abort() ou _Exit()','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3232','160','RECOMMANDATION — Limiter les appels à exit()','Les appels à la fonction exit() doivent être commentés et non systématiques. Le développeur doit le plus souvent possible les remplacer par un retour de code d’erreur  dans la fonction principale.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3233','161','RÈGLE — Ne pas utiliser les fonctions setjmp() et longjump()','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3234','162','RÈGLE — Ne pas utiliser les bibliothèques standards setjmp.h et stdarg.h','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3235','163','RECOMMANDATION — Limiter l''utilisation des bibliothèques standards manipulant des nombres flottants','Les bibliothèques standards float.h, fenv.h, complex.h et math.h ne doivent être utilisées que si cela est vraiment nécessaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3236','164','RÈGLE — Ne pas utiliser les fonctions atoi() atol() atof() et atoll() de la bibliothèque stdlib.h','Les fonctions équivalentes strto*() sont à utiliser en remplacement.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3237','165','RÈGLE — Ne pas utiliser la fonction rand() de la bibliothèque standard','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3238','166','RÈGLE — Utiliser les versions « plus sécurisées » pour les fonctions de la bibliothèque standard','Quand des fonctions de la bibliothèque standard existent en différentes versions, la version « plus sécurisée » doit être utilisée.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3239','167','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque obsolescentes ou devenues obsolètes dans des','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3240','168','RÈGLE — Ne pas utiliser de fonctions de la bibliothèque manipulant des buffers sans prendre la taill','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3241','169','BONNE PRATIQUE — Tout code doit être soumis à relecture','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3242','170','RECOMMANDATION — Indentation des expressions longues','Lorsqu’une instruction ou une expression s’étale sur plusieurs lignes, il est indispensable de l’indenter afin de faciliter la compréhension du code.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3243','171','RÈGLE — Identifier et supprimer tout code mort','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3244','172','RÈGLE — Le code doit être exempt de code non atteignable en dehors de code défensif et de code d''int','Il ne doit jamais y avoir de code inatteignable, sauf s’il s’agit de code défensif ou s’il s’agit de code d’une interface et dans ces deux cas, il faut le préciser en commentaire.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3245','173','RECOMMANDATION — Evaluation outillée du code source pour limiter les risques d''erreurs d''exécution','Le code source du logiciel doit être analysé via au moins un outil d’analyse de code. Les résultats produits par l’outil d’analyse doivent être étudiés par le développeur et les corrections doivent être effectuées par rapport aux problèmes découverts.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3246','174','RECOMMANDATION — Limitation de la complexité cyclomatique','La complexité cyclomatique d’une fonction doit être limitée au maximum.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3247','175','RECOMMANDATION — Limitation de la longueur et la complexité d''une fonction','Une fonction doit être associée idéalement à un seul et unique traitement et doit donc correspondre à un nombre de lignes de code raisonnable.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3248','176','RÈGLE — Ne pas utiliser de mots clés du C++','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3249','177','RÈGLE — Séquences de caractères interdites dans les commentaires','Les séquences /* et // sont interdites dans tous les commentaires. Et un commentaire sur une ligne introduit par // ne doit pas contenir de caractère de continuation de ligne \\.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3250','178','RÈGLE — Mise en oeuvre manuelle de mécanismes « canari » si les options de durcissement ne sont pas ','Vide',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3251','179','RÈGLE — Pas d''assertions de mise au point sur un code mis en production','Les assertions de mise au point ne doivent pas être présentes en production.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3252','180','RECOMMANDATION — La gestion des assertions d''intégrité doit inclure un effacement des données d''urge','Les assertions d’intégrité doivent apparaître en production. En cas de déclenchement d’une assertion d’intégrité, le code de traitement doit aboutir à un effacement d’urgence des données sensibles.',NULL,NULL,NULL,NULL,'33','62','1.d');
INSERT INTO `O_regle` VALUES ('3253','181','RÈGLE — Tout fichier non vide doit se terminer par un retour à la ligne et les directives de preproc','Un fichier non vide ne doit pas se terminer au milieu d’un commentaire ou d’une directive de preprocessing.',NULL,NULL,NULL,NULL,'33','62','1.d');
